// HelloWebSocket.jsx
import React, { useState, useEffect } from 'react';
import { connectWebSocket, disconnectWebSocket, sendWebSocketMessage } from '../websocket';

function HelloWebSocket() {
    const [message, setMessage] = useState('');
    const [response, setResponse] = useState('');

    useEffect(() => {
        connectWebSocket((msg) => {
            setResponse(msg); // Hier msg direkt setzen
        });

        return () => {
            disconnectWebSocket();
        };
    }, []);

    const sendMessage = () => {
        sendWebSocketMessage('/app/hello', message);
    };

    return (
        <div>
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Enter your message"
            />
            <button onClick={sendMessage}>Send</button>
            <div>
                <h3>Response:</h3>
                <p>{response}</p>
            </div>
        </div>
    );
}

export default HelloWebSocket;
