import React, { useState, useEffect, useCallback } from 'react';
import { connectWebSocket, disconnectWebSocket, sendUpdateTaskMessage } from '../websocket';

const HelloWebSocket = () => {
    const [tasks, setTasks] = useState([]);
    const [userId, setUserId] = useState(null);

    const handleWebSocketMessage = useCallback((message) => {
        console.log('Received WebSocket message:', message);
        if (message.type === 'TASK_UPDATE') {
            console.log('Handling TASK_UPDATE message:', message);
            setTasks((prevTasks) => {
                const existingTaskIndex = prevTasks.findIndex((task) => task.taskId === message.task.taskId);
                if (existingTaskIndex > -1) {
                    const updatedTasks = [...prevTasks];
                    updatedTasks[existingTaskIndex] = message.task;
                    console.log('Updated tasks:', updatedTasks);
                    return updatedTasks;
                } else {
                    console.log('Adding new task to tasks:', message.task);
                    return [...prevTasks, message.task];
                }
            });
        }
    }, []);

    useEffect(() => {
        const userId = 1; // Setze deine UserID hier
        setUserId(userId);
        console.log('Connecting WebSocket for user ID:', userId);
        connectWebSocket(handleWebSocketMessage);

        return () => {
            console.log('Disconnecting WebSocket for user ID:', userId);
            disconnectWebSocket();
        };
    }, [handleWebSocketMessage]);

    const handleCheckboxChange = (taskId) => {
        setTasks((prevTasks) =>
            prevTasks.map((task) =>
                task.taskId === taskId ? { ...task, completed: !task.completed } : task
            )
        );

        const updatedTask = tasks.find((task) => task.taskId === taskId);
        if (updatedTask) {
            console.log('Sending WebSocket message to update task:', { ...updatedTask, userId });
            sendUpdateTaskMessage({ ...updatedTask, userId });
        }
    };

    return (
        <div>
            <h1>WebSocket Task Updates</h1>
            <ul>
                {tasks.map((task) => (
                    <li key={task.taskId}>
                        <input
                            type="checkbox"
                            checked={task.completed}
                            onChange={() => handleCheckboxChange(task.taskId)}
                        />
                        {task.text}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default HelloWebSocket;
