import React, { useState, useEffect } from 'react';
import ToDo from './ToDo';
import { startOfToday, isAfter } from 'date-fns';
import { deleteTask, updateTask } from '../api'; // Importieren der API-Funktionen zum Löschen und Aktualisieren von Aufgaben

export default function ToDoList({ todos = [], setTasks, isDeleteMode, onEditTodo, selectedCategory }) {
  const [selectedTodoId, setSelectedTodoId] = useState(null); // State für die ausgewählte Aufgaben-ID
  const [localTodos, setLocalTodos] = useState(todos); // Lokaler State für die Aufgaben

  // Effekt, der bei jeder Änderung der todos-Liste ausgeführt wird
  useEffect(() => {
    setLocalTodos(todos);
    console.log('Todos updated:', todos);
  }, [todos]);

  // Funktion zum Bearbeiten des Status der Checkbox einer Aufgabe
  const handleCheckboxChange = async (id) => {
    console.log('Checkbox changed for ID:', id);
    const updatedTodos = localTodos.map(todo => {
      if (todo.taskId === id) {
        const updatedTodo = { ...todo, completed: !todo.completed };
        if (updatedTodo.completed) {
          updatedTodo.categoryId = 7; // Kategorie-ID auf 7 setzen, wenn die Aufgabe abgeschlossen ist
        }
        return updatedTodo;
      }
      return todo;
    });
    setLocalTodos(updatedTodos); // Lokalen State aktualisieren

    const updatedTask = updatedTodos.find(todo => todo.taskId === id); // Aktualisierte Aufgabe finden
    try {
      await updateTask(updatedTask); // WebSocket: API-Aufruf zum Aktualisieren der Aufgabe
      console.log('Fetching tasks after checkbox change...');
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  // Funktion zum Löschen einer Aufgabe
  const handleDeleteClick = async (id) => {
    console.log('Deleting task with ID:', id);
    try {
      await deleteTask(id); // REST: API-Aufruf zum Löschen der Aufgabe
      console.log('Fetching tasks after delete...');
    } catch (error) {
      console.error('Error deleting task:', error.response ? error.response.data : error.message);
    }
  };

  // Funktion, die aufgerufen wird, wenn auf eine Aufgabe geklickt wird
  const handleTodoClick = (id) => {
    console.log('Todo clicked with ID:', id);
    setSelectedTodoId(id); // Ausgewählte Aufgaben-ID im State setzen
  };

  // Funktion zum Bearbeiten einer Aufgabe
  const handleEditClick = (todo) => {
    onEditTodo(todo); // Funktion zum Bearbeiten der Aufgabe aufrufen
  };

  // Funktion zum Ändern des Datums einer Aufgabe
  const handleDateChange = async (id, date, purpose) => {
    const formattedDate = date.toISOString().split('T')[0]; // Datum im Format "yyyy-MM-dd" formatieren
    console.log(`Updating ${purpose} date to:`, formattedDate);
    const updatedTodos = localTodos.map(todo =>
      todo.taskId === id ? { ...todo, [`${purpose}Date`]: formattedDate } : todo
    );
    setLocalTodos(updatedTodos); // Lokalen State aktualisieren

    const updatedTask = updatedTodos.find(todo => todo.taskId === id); // Aktualisierte Aufgabe finden
    try {
      await updateTask(updatedTask); // WebSocket: API-Aufruf zum Aktualisieren der Aufgabe
      console.log('Fetching tasks after date change...');
      await fetchTasksData(); // REST: Aktualisieren der Aufgabenliste nach der Aktualisierung
    } catch (error) {
      console.error('Error updating date:', error);
    }
  };

  const today = startOfToday(); // Heutiges Datum

  // Filtern der Aufgaben basierend auf der ausgewählten Kategorie
  const filteredTodos = localTodos.filter(todo => {
    switch (selectedCategory.name) {
      case 'Planned':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
      case 'Today':
      case 'Personal':
      case 'Work':
      case 'Any Time':
        return todo.categoryId === selectedCategory.id;
      case 'All Tasks':
        return !todo.planDate && todo.categoryId !== 7;
      case 'Logbook':
        return todo.categoryId === 7;
      default:
        return todo.categoryId === selectedCategory.id;
    }
  });

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => (
          <ToDo
            key={todo.taskId} // Verwendung der taskId als Schlüssel
            todo={todo}
            handleCheckboxChange={() => handleCheckboxChange(todo.taskId)}
            handleTodoClick={() => handleTodoClick(todo.taskId)}
            handleDelete={() => handleDeleteClick(todo.taskId)}
            handleEditClick={() => handleEditClick(todo)}
            isSelected={todo.taskId === selectedTodoId} // Übergeben des ausgewählten Zustands
          />
        ))}
      </div>
    </fieldset>
  );
}
