import React, { useState } from 'react';
import AvatarGroup from './AvatarGroup';
import { XIcon, EditIcon, LinkIcon, LinkSelectedIcon, PlanTaskIcon, PlanTaskGreyIcon, PlannedIcon } from '../icons';
import Calendar from './Calendar';
import { generateTokenForTask } from '../api';

export default function ToDo({ todo, handleCheckboxChange, handleTodoClick, handleDelete, handleEditClick, isDeleteMode, onDateChange, isTodayCategory, isSelected }) {
  const [showCalendar, setShowCalendar] = useState(false);
  const [calendarPurpose, setCalendarPurpose] = useState(null);
  const [isHovered, setIsHovered] = useState(false);
  const [shareText, setShareText] = useState('Share Task');
  const [shareTextColor, setShareTextColor] = useState('text-gray-500');
  const [showTooltip, setShowTooltip] = useState(false);

  const handleDateClick = (e, purpose) => {
    e.stopPropagation();
    setCalendarPurpose(purpose);
    setShowCalendar(!showCalendar);
  };

  const handleDateChange = async (date) => {
    try {
      await onDateChange(todo.taskId, date, calendarPurpose);
      setShowCalendar(false);
    } catch (error) {
      console.error('Error setting date:', error);
    }
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const handleShareTaskClick = async (e) => {
    e.stopPropagation();
    console.log('Selected Task ID:', todo.taskId); // Log the selected taskId
    if (!todo.taskId) {
      console.error('No task selected.');
      return;
    }
    try {
      const data = await generateTokenForTask(todo.taskId);
      const linkWithToken = `http://localhost:8080/api/v1/token/${data.token}`;

      await navigator.clipboard.writeText(linkWithToken);
      setShowTooltip(true);
      setShareText('Copied.');
      setShareTextColor('text-[#0374FA]');
      setTimeout(() => {
        setShareText('Share Task');
        setShareTextColor('text-gray-500');
        setShowTooltip(false);
      }, 1000);
    } catch (err) {
      console.error('Failed to copy: ', err);
      setShareText('Error');
      setShareTextColor('text-red-500');
      setTimeout(() => {
        setShareText('Share Task');
        setShareTextColor('text-gray-500');
      }, 1000);
    }
  };

  const handleTodoClickInternal = (e) => {
    e.stopPropagation();
    handleTodoClick(todo.taskId === isSelected ? null : todo.taskId);
  };

  return (
    <div
      key={todo.taskId}
      className={`todo-item ${isSelected ? 'selected' : ''} p-2 rounded-lg transition-all duration-300 ease-in-out ${isHovered ? 'bg-gray-100' : ''}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={handleTodoClickInternal}
    >
      <div className="flex items-start relative">
        <div className="flex h-6 items-center">
          <input
            id={`comments${todo.taskId}`}
            name={`comments${todo.taskId}`}
            type="checkbox"
            className="h-4 w-4 rounded border-gray-300 text-blue-500 focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 focus:border-blue-500"
            checked={todo.completed || false}
            onChange={() => handleCheckboxChange(todo.taskId)}
          />
        </div>
        <div className="todo-label ml-3 text-sm leading-6 flex items-center">
          <label htmlFor={`comments${todo.taskId}`} className="font-medium">
            {todo.text}
          </label>
          <EditIcon
            className={`ml-2 h-4 w-4 text-gray-500 hover:text-gray-700 cursor-pointer ${isHovered ? 'block' : 'hidden'}`}
            onClick={(e) => {
              e.stopPropagation();
              handleEditClick(todo);
            }}
          />
          <div
            className={`ml-2 h-4 w-4 text-gray-500 cursor-pointer relative ${isHovered ? 'block' : 'hidden'}`}
            onClick={handleShareTaskClick}
          >
            <LinkIcon className="h-4 w-4 text-gray-500" />
            {showTooltip && (
              <div className="absolute left-1/2 transform -translate-x-1/2 mt-2 px-2 py-1 text-xs text-[#0673FA] bg-[#F2F2F2] rounded">
                Copied
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="flex items-center space-x-2 ml-3" style={{ marginRight: isDeleteMode ? '40px' : '0' }}>
        {todo.planDate && (
          <div className="flex items-center gap-1" onClick={(e) => handleDateClick(e, 'plan')}>
            <PlannedIcon className="h-5 w-5 cursor-pointer" />
            <span className={`font-medium text-sm cursor-pointer ${todo.completed ? 'text-gray-600' : 'text-gray-900'}`}>
              {new Date(todo.planDate).toLocaleDateString('de-DE', { day: 'numeric', month: 'short' })}
            </span>
          </div>
        )}
        <div className="flex items-center gap-1" onClick={(e) => handleDateClick(e, 'deadline')}>
          {todo.deadlineDate ? (
            <PlanTaskIcon className="h-5 w-5 cursor-pointer" />
          ) : (
            <PlanTaskGreyIcon className="h-5 w-5 cursor-pointer" />
          )}
          {todo.deadlineDate && (
            <span className={`font-medium text-sm cursor-pointer ${todo.completed ? 'text-gray-600' : 'text-gray-900'}`}>
              {new Date(todo.deadlineDate).toLocaleDateString('de-DE', { day: 'numeric', month: 'short' })}
            </span>
          )}
          {showCalendar && (
            <div className="absolute mt-2">
              <Calendar
                selectedDate={new Date(todo[calendarPurpose === 'plan' ? 'planDate' : 'deadlineDate'] || new Date())}
                setSelectedDate={handleDateChange}
                setShowCalendar={setShowCalendar}
              />
            </div>
          )}
        </div>
        <div className="pl-2">
          <AvatarGroup
            assignedUsers={todo.assignedUsers}
            users={[]} // Placeholder f端r initialUsers
            selected={false} // Nicht verwendet f端r Auswahllogik
          />
        </div>
      </div>
      {isDeleteMode && (
        <button
          type="button"
          className="absolute right-2 text-red-500"
          onClick={(e) => {
            e.stopPropagation();
            handleDelete(todo.taskId);
          }}
        >
          <XIcon className="h-5 w-5" />
        </button>
      )}
    </div>
  );
}
