export const STANDARD_CATEGORIES = [
  { id: 1, name: 'All Tasks' },
  { id: 2, name: 'Today' },
  { id: 3, name: 'Planned' },
  { id: 4, name: 'Any Time' },
  { id: 5, name: 'Work' },
  { id: 6, name: 'Personal' },
  { id: 7, name: 'Logbook' },
];

export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  categories.forEach(category => {
    switch (category.id) {
      case 1:
        // All Tasks
        categorizedTasks[category.id] = tasks;
        break;
      case 2:
        // Today
        const today = new Date().toISOString().split('T')[0];
        categorizedTasks[category.id] = tasks.filter(task => task.planDate === today);
        break;
      case 3:
        // Planned
        categorizedTasks[category.id] = tasks.filter(task => task.planDate);
        break;
      case 4:
        // Any Time (just a folder, no tasks)
        categorizedTasks[category.id] = [];
        break;
      case 5:
        // Work
        categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
        break;
      case 6:
        // Personal
        categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
        break;
      case 7:
        // Logbook
        categorizedTasks[category.id] = tasks.filter(task => task.completed);
        break;
      default:
        // Handle any unknown categories if necessary
        categorizedTasks[category.id] = [];
        break;
    }
  });

  return categorizedTasks;
};
