import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

class WebSocketService {
  constructor() {
    this.stompClient = null;
    this.connected = false;
    this.reconnectDelay = 5000; // 5 seconds
  }

  connect(onTaskMessageReceived, onCategoryMessageReceived, userId) {
    const socket = new SockJS(SOCKET_URL);
    this.stompClient = Stomp.over(socket);

    this.stompClient.connect({}, () => {
      console.log('Connected to WebSocket');
      this.connected = true;
      this.stompClient.subscribe(`/topic/tasks`, (message) => {
        console.log('Task message received:', message.body);
        onTaskMessageReceived(JSON.parse(message.body));
      });
      this.stompClient.subscribe(`/topic/categories`, (message) => {
        console.log('Category message received:', message.body);
        onCategoryMessageReceived(JSON.parse(message.body));
      });
    }, (error) => {
      console.error('Error connecting to WebSocket:', error);
      this.connected = false;
      setTimeout(() => this.connect(onTaskMessageReceived, onCategoryMessageReceived, userId), this.reconnectDelay);
    });
  }

  disconnect() {
    if (this.stompClient) {
      this.stompClient.disconnect(() => {
        console.log('Disconnected from WebSocket');
        this.connected = false;
      });
    }
  }

  sendMessage(destination, message) {
    if (this.stompClient && this.connected) {
      console.log('Sending message:', message);
      this.stompClient.send(destination, {}, JSON.stringify(message));
    } else {
      console.error('Cannot send message: WebSocket not connected');
    }
  }
}

const webSocketService = new WebSocketService();

export const connectWebSocket = (onTaskMessageReceived, onCategoryMessageReceived, userId) => {
  webSocketService.connect(onTaskMessageReceived, onCategoryMessageReceived, userId);
};

export const disconnectWebSocket = () => {
  webSocketService.disconnect();
};

export const sendWebSocketMessage = (destination, message) => {
  webSocketService.sendMessage(destination, message);
};

export default webSocketService;
