import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';

const ShareHandler = () => {
  const { token } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuthAndFetchDetails = async () => {
      const jwtToken = Cookies.get('jwtToken');
      const isLoggedIn = !!jwtToken;

      if (!isLoggedIn) {
        navigate(`/login?returnUrl=/share/${token}`);
      } else {
        try {
          const response = await axios.get(`http://localhost:8080/api/v1/token/share/${token}`, {
            headers: {
              Authorization: `Bearer ${jwtToken}`,
            },
          });
          console.log('Share details:', response.data);
          const { userId, sharedTaskId, sharedCategoryId } = response.data;

          // Speichern der Share-Details in der Session oder einem globalen Zustand
          sessionStorage.setItem('sharedTaskId', sharedTaskId);
          sessionStorage.setItem('sharedCategoryId', sharedCategoryId);

          // Weiterleitung zum Dashboard
          navigate('/dashboard');
        } catch (error) {
          if (error.response && error.response.status === 401) {
            navigate(`/login?returnUrl=/share/${token}`);
          } else {
            console.error('Error fetching share details:', error);
          }
        }
      }
    };

    checkAuthAndFetchDetails();

    return () => {
      // Cleanup
    };
  }, [token, navigate]);

  return (
    <div>
      <h1>Processing Share Link...</h1>
    </div>
  );
};

export default ShareHandler;
