import React, { useState, useRef, useEffect, useCallback } from 'react';
import { format } from 'date-fns';
import { AddTodoIcon, PlanTaskIcon, PlannedIcon } from '../icons';
import Calendar from './Calendar';

export default function AddTaskPopUp({ onAddTask, onClose, currentCategoryId }) {
  const [taskText, setTaskText] = useState('');
  const [planDate, setPlanDate] = useState(null);
  const [deadlineDate, setDeadlineDate] = useState(null);
  const [showCalendar, setShowCalendar] = useState(false);
  const [showTooltip, setShowTooltip] = useState(false);
  const [calendarPurpose, setCalendarPurpose] = useState(null); // 'plan' or 'deadline'
  const ref = useRef(null);

  const handleClickOutside = useCallback((event) => {
    if (ref.current && !ref.current.contains(event.target)) {
      onClose();
    }
  }, [onClose]);

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [handleClickOutside]);

  const handleAddTask = async (e) => {
    e.preventDefault();
    if (taskText.trim() === '') {
      setShowTooltip(true);
      return;
    }
    const newTask = {
      text: taskText,
      categoryId: currentCategoryId || null,
      planDate: planDate ? format(planDate, 'yyyy-MM-dd') : null,
      deadlineDate: deadlineDate ? format(deadlineDate, 'yyyy-MM-dd') : null,
    };

    await onAddTask(newTask);

    // Reset form state after adding task
    setTaskText('');
    setPlanDate(null);
    setDeadlineDate(null);
    setShowTooltip(false);
    onClose();
  };

  const handleDateChange = (date) => {
    if (calendarPurpose === 'plan') {
      setPlanDate(date);
    } else if (calendarPurpose === 'deadline') {
      setDeadlineDate(date);
    }
    setShowCalendar(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-gray-900 bg-opacity-50">
      <div ref={ref} className="bg-white rounded-lg shadow-lg relative p-4 border border-gray-300 w-full max-w-lg">
        <form className="flex flex-col space-y-4" onSubmit={handleAddTask}>
          <input
            type="text"
            placeholder="Type in what you have to do"
            className={`flex-grow p-2 border rounded-lg bg-white text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 ${showTooltip ? 'border-red-500' : 'border-gray-300'}`}
            value={taskText}
            onChange={(e) => setTaskText(e.target.value)}
          />
          {showTooltip && (
            <div className="text-sm text-red-500 bg-white border border-red-500 rounded-lg p-2 shadow-lg">
              To Do Input cannot be empty.
            </div>
          )}
          <div className="flex space-x-2">
            <button
              type="button"
              className="flex items-center justify-center gap-1 flex-grow h-10 rounded-lg text-gray-700 font-medium bg-gray-200 hover:bg-gray-300"
              onClick={() => {
                setCalendarPurpose('deadline');
                setShowCalendar(true);
              }}
            >
              <PlanTaskIcon className="h-5 w-5" />
              {deadlineDate ? format(new Date(deadlineDate), 'MMMM d') : 'Deadline'}
            </button>
            <button
              type="button"
              onClick={() => {
                setCalendarPurpose('plan');
                setShowCalendar(true);
              }}
              className="flex items-center justify-center gap-1 flex-grow h-10 rounded-lg text-pink-700 font-medium bg-pink-200 hover:bg-pink-300"
            >
              <PlannedIcon className="h-5 w-5 text-pink-700" />
              {planDate ? format(new Date(planDate), 'MMMM d') : 'Plan'}
            </button>
            <button
              type="submit"
              className="flex items-center justify-center gap-1 flex-grow h-10 rounded-lg text-blue-700 font-medium bg-blue-200 hover:bg-blue-300"
            >
              <AddTodoIcon className="h-5 w-5" />
              Add Task
            </button>
          </div>
          {showCalendar && (
            <div className="absolute right-0 mt-2">
              <Calendar
                selectedDate={calendarPurpose === 'plan' ? planDate : deadlineDate}
                setSelectedDate={handleDateChange}
                setShowCalendar={setShowCalendar}
              />
            </div>
          )}
        </form>
      </div>
    </div>
  );
}
