import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp';
import { fetchTasks, addTask, deleteTask, fetchUserId, fetchEmail, addCategory, fetchCategories, deleteCategory } from '../api';
import { connectWebSocket, disconnectWebSocket, sendUpdateTaskMessage } from '../websocket'; 
import { iconMap, customCategoryIcon } from '../icons';

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  const initializeDashboard = useCallback(async () => {
    try {
      const email = await fetchEmail();
      const fetchedUserId = await fetchUserId(email);

      if (fetchedUserId) {
        setUserId(fetchedUserId);

        const fetchedTasks = await fetchTasks();
        console.log('Fetched Tasks:', fetchedTasks);
        setTasks(fetchedTasks);

        const fetchedCategories = await fetchCategories(fetchedUserId);
        const uniqueCategories = fetchedCategories.reduce((acc, category) => {
          if (!acc.some(cat => cat.categoryId === category.categoryId)) {
            acc.push({
              ...category,
              icon: iconMap[category.name] || customCategoryIcon,
            });
          }
          return acc;
        }, []);
        console.log('Fetched Categories:', uniqueCategories);
        setCategories(uniqueCategories);

        if (uniqueCategories.length > 0) {
          setSelectedCategory(uniqueCategories[0]);
        } else {
          setSelectedCategory(null);
        }
      } else {
        navigate('/login');
      }
    } catch (error) {
      console.error('Error initializing dashboard:', error);
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    initializeDashboard();
  }, [initializeDashboard]);

  useEffect(() => {
    const handleWebSocketMessage = (message) => {
      console.log('Received WebSocket message:', message);

      const { type, taskId, ...updatedData } = message;

      if (type === 'TASK_UPDATE') {
        setTasks(prevTasks => {
          const taskIndex = prevTasks.findIndex(task => task.taskId === taskId);
          if (taskIndex !== -1) {
            const updatedTasks = [...prevTasks];
            updatedTasks[taskIndex] = { ...prevTasks[taskIndex], ...updatedData };
            console.log('Updated Tasks:', updatedTasks);
            return updatedTasks;
          } else {
            const newTasks = [...prevTasks, { taskId, ...updatedData }];
            console.log('New Tasks:', newTasks);
            return newTasks;
          }
        });
      }
    };

    if (userId) {
      console.log('Connecting WebSocket for user ID:', userId);

      const fetchIds = async () => {
        try {
          const fetchedTasks = await fetchTasks();
          const taskIds = fetchedTasks.map(task => task.taskId);
          connectWebSocket(userId, taskIds, [], handleWebSocketMessage);
        } catch (error) {
          console.error('Error fetching IDs for WebSocket:', error);
        }
      };

      fetchIds();

      return () => {
        console.log('Disconnecting WebSocket for user ID:', userId);
        disconnectWebSocket();
      };
    }
  }, [userId]);

  const handleAddNewTask = async (newTask) => {
    if (!selectedCategory) return;

    try {
      const formattedTask = {
        ...newTask,
        categoryId: selectedCategory.categoryId,
        userId: userId
      };
      console.log("Formatted Task before sending to backend:", formattedTask);

      const addedTask = await addTask(formattedTask, userId);
      setShowAddTaskPopUp(false);

      setTasks(prevTasks => {
        const newTasks = [...prevTasks, addedTask];
        console.log('Tasks after adding new task:', newTasks);
        return newTasks;
      });
      sendUpdateTaskMessage(addedTask);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleSaveTask = async (updatedTask) => {
    try {
      const taskToUpdate = {
        ...updatedTask,
        userId: userId,
        taskId: currentTask.taskId,
        categoryId: currentTask.categoryId,
      };
      console.log("Task to Update before sending to backend:", taskToUpdate);
      setShowEditTaskPopUp(false);
      setTasks(prevTasks => {
        const updatedTasks = prevTasks.map(task => (task.taskId === taskToUpdate.taskId ? taskToUpdate : task));
        console.log('Tasks after updating task:', updatedTasks);
        return updatedTasks;
      });
      sendUpdateTaskMessage(taskToUpdate);
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      const updatedTask = tasks.find(task => task.taskId === taskId);
      if (updatedTask) {
        updatedTask.completed = true;
        updatedTask.categoryId = 7; // Manuelles Setzen der Kategorie-ID im Frontend
        setTasks(prevTasks => {
          const updatedTasks = prevTasks.map(task =>
            task.taskId === taskId ? updatedTask : task
          );
          console.log('Tasks after completing task:', updatedTasks);
          return updatedTasks;
        });
        sendUpdateTaskMessage(updatedTask); // WebSocket-Nachricht senden
      }
    } catch (error) {
      console.error('Error completing task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      setTasks(prevTasks => {
        const updatedTasks = prevTasks.filter(task => task.taskId !== id);
        console.log('Tasks after deleting task:', updatedTasks);
        return updatedTasks;
      });
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleAddCategory = async (newCategory) => {
    try {
      const data = await addCategory(newCategory);
      const newCategoryObject = {
        categoryId: data.categoryId,
        name: data.name,
        icon: customCategoryIcon,
      };
      setCategories(prevCategories => {
        const updatedCategories = [...prevCategories, newCategoryObject];
        console.log('Categories after adding new category:', updatedCategories);
        return updatedCategories;
      });
      setSelectedCategory(newCategoryObject);
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const handleDeleteCategory = async (id) => {
    try {
      await deleteCategory(id);
      setCategories(prevCategories => {
        const updatedCategories = prevCategories.filter(category => category.categoryId !== id);
        console.log('Categories after deleting category:', updatedCategories);
        return updatedCategories;
      });
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(prevMode => !prevMode);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true);
  };

  const fetchTasksForSelectedCategory = useCallback(async () => {
    if (!selectedCategory) return;
    try {
      const fetchedTasks = await fetchTasks();
      console.log('Fetched Tasks for selected category:', fetchedTasks);
      setTasks(fetchedTasks);
    } catch (error) {
      console.error('Error fetching tasks for selected category:', error);
    }
  }, [selectedCategory]);

  useEffect(() => {
    fetchTasksForSelectedCategory();
  }, [selectedCategory, fetchTasksForSelectedCategory]);

  const filteredTasks = useMemo(() => {
    if (!selectedCategory) return tasks;
    switch (selectedCategory.name) {
      case 'All Tasks':
        return tasks.filter(task => !task.completed);
      case 'Today':
        return tasks.filter(task => new Date(task.planDate).toDateString() === new Date().toDateString());
      case 'Planned':
        return tasks.filter(task => task.planDate !== null);
      case 'Any Time':
        return tasks.filter(task => task.planDate === null && !task.completed && task.categoryId === selectedCategory.categoryId);
      case 'Work':
        return tasks.filter(task => task.categoryId === selectedCategory.categoryId);
      case 'Personal':
        return tasks.filter(task => task.categoryId === selectedCategory.categoryId);
      case 'Logbook':
        return tasks.filter(task => task.categoryId === 7); // Entfernen der completed-Bedingung
      default:
        return tasks.filter(task => task.categoryId === selectedCategory.categoryId);
    }
  }, [tasks, selectedCategory]);

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories
            onSelectCategory={setSelectedCategory}
            userId={userId}
            categoriesFromBackend={categories}
          />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              {selectedCategory && selectedCategory.icon && <selectedCategory.icon className="h-6 w-6" />}
              <span className="font-bold text-grayCustom-dark">{selectedCategory?.name}</span>
            </div>
            <ToDoList
              todos={filteredTasks}
              setTasks={setTasks}
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTask}
              onCompleteTask={handleCompleteTask} // Add this prop
              selectedCategory={selectedCategory}
              handleDeleteTask={handleDeleteTask}
              userId={userId}
            />
            <ToDoActions
              toggleAddTaskModal={() => setShowAddTaskPopUp(true)}
              toggleDeleteMode={toggleDeleteMode}
              toggleAddCategoryModal={() => setShowAddCategoryPopUp(true)}
              handleDeleteCategory={handleDeleteCategory}
              selectedCategoryId={selectedCategory?.categoryId}
              userId={userId}
            />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAddTask={handleAddNewTask}
          categories={categories}
          onClose={() => setShowAddTaskPopUp(false)}
        />
      )}
      {showEditTaskPopUp && (
        <EditTaskPopUp
          task={currentTask}
          onSaveTask={handleSaveTask}
          onClose={() => setShowEditTaskPopUp(false)}
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
          userId={userId}
        />
      )}
    </div>
  );
}

export default Dashboard;
