export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  // Initialize categorizedTasks for each category
  categories.forEach(category => {
    categorizedTasks[category.categoryId] = [];
  });

  // Initialize standard categories
  const standardCategories = [1, 2, 3, 4, 5, 6, 7];
  standardCategories.forEach(id => {
    if (!categorizedTasks[id]) {
      categorizedTasks[id] = [];
    }
  });

  // Filter tasks based on category rules
  tasks.forEach(task => {
    const { categoryId, completed, planDate } = task;

    // Debugging: Log task information
    console.log(`Processing task with ID: ${task.taskId}, categoryId: ${categoryId}`);

    // Always add task to its specific category
    if (!categorizedTasks[categoryId]) {
      categorizedTasks[categoryId] = [];
    }
    categorizedTasks[categoryId].push(task);

    // Add tasks to standard categories based on conditions
    if (categoryId !== 7) {
      categorizedTasks[1].push(task); // All Tasks (excluding Logbook)
    }
    if (completed) {
      categorizedTasks[7].push(task); // Logbook
    } else {
      if (categoryId === 2) {
        categorizedTasks[2].push(task); // Today
      }
      if (planDate) {
        categorizedTasks[3].push(task); // Planned
      }
      if (categoryId === 4) {
        categorizedTasks[4].push(task); // Any Time
      }
      if (categoryId === 5) {
        categorizedTasks[5].push(task); // Work
      }
      if (categoryId === 6) {
        categorizedTasks[6].push(task); // Personal
      }
    }
  });

  // Debugging: Log categorized tasks
  console.log('Categorized tasks:', categorizedTasks);

  return categorizedTasks;
};
