import React, { useState } from 'react';
import AvatarGroup from './AvatarGroup';
import { XIcon, EditIcon, LinkIcon, LinkSelectedIcon, PlanTaskIcon, PlanTaskGreyIcon, PlannedIcon } from '../icons';
import Calendar from './Calendar';
import { generateTokenForTask } from '../api';

export default function ToDo({ todo, handleCheckboxChange, handleTodoClick, handleDelete, handleEditClick, isDeleteMode, initialUsers, onDateChange, isTodayCategory }) {
  const [shareText, setShareText] = useState('Share');
  const [shareTextColor, setShareTextColor] = useState('text-gray-500');
  const [showCalendar, setShowCalendar] = useState(false);
  const [calendarPurpose, setCalendarPurpose] = useState(null); // 'plan' or 'deadline'
  const [selectedTodoId, setSelectedTodoId] = useState(null);
  const [isHovered, setIsHovered] = useState(false);
  const [isClicked, setIsClicked] = useState(false);

  const handleShareTaskClick = async (todo) => {
    try {
      const response = await fetch(`/api/generate-share-token/${todo.id}`, { method: 'POST' });
      const data = await response.json();
      const linkWithToken = `https://example.com/share?token=${data.token}`;

      await navigator.clipboard.writeText(linkWithToken);
      setShareText('Copied.');
      setShareTextColor('text-[#0374FA]');
      setIsClicked(true);
      setTimeout(() => {
        setShareText('Share');
        setShareTextColor('text-gray-500');
        setIsClicked(false);
      }, 1000);
    } catch (err) {
      console.error('Failed to copy: ', err);
      setShareText('Error');
      setShareTextColor('text-red-500');
      setIsClicked(false);
      setTimeout(() => {
        setShareText('Share');
        setShareTextColor('text-gray-500');
      }, 1000);
    }
  };

  const handleDateClick = (e, purpose, id) => {
    e.stopPropagation();
    setCalendarPurpose(purpose);
    setShowCalendar(!showCalendar || selectedTodoId !== id);
    setSelectedTodoId(id);
  };

  const handleDateChange = async (date) => {
    try {
      const response = await fetch(`/api/set-date/${todo.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          date,
          purpose: calendarPurpose,
        }),
      });

      if (response.ok) {
        onDateChange(todo.id, date, calendarPurpose);
        setShowCalendar(false); // Close the calendar after selecting a date
        setSelectedTodoId(null);
      } else {
        throw new Error('Failed to set date');
      }
    } catch (error) {
      console.error('Error setting date:', error);
    }
  };

  const handleLinkMouseEnter = () => {
    setIsHovered(true);
  };

  const handleLinkMouseLeave = () => {
    setIsHovered(false);
  };

  const handleLinkClick = (e) => {
    e.stopPropagation();
    handleShareTaskClick(todo);
  };

  const handleTodoClickInternal = (e) => {
    e.stopPropagation();
    handleTodoClick(todo.taskId === selectedTodoId ? null : todo.taskId);
  };

  return (
    <div
      key={todo.id}
      className={`relative flex items-center justify-between p-2 rounded-lg ${todo.selected ? 'bg-blue-100' : 'hover:bg-blue-50'} group`}
      style={{ paddingTop: '3px', paddingBottom: '3px' }}
      onClick={handleTodoClickInternal}
    >
      <div className="flex items-start relative">
        <div className="flex h-6 items-center">
          <input
            id={`comments${todo.id}`}
            aria-describedby={`comments-description${todo.id}`}
            name={`comments${todo.id}`}
            type="checkbox"
            className={`h-4 w-4 rounded border-gray-300 text-blue-500 focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 focus:border-blue-500`}
            checked={todo.completed || false}
            onChange={() => handleCheckboxChange(todo.id)}
          />
        </div>
        <div className={`ml-3 text-sm leading-6 ${todo.completed && isTodayCategory ? 'text-gray-600 opacity-50' : 'text-gray-900'} flex items-center`}>
          <label htmlFor={`comments${todo.id}`} className="font-medium">
            {todo.text}
          </label>
          <EditIcon
            className={`ml-2 h-4 w-4 text-gray-500 hover:text-gray-700 cursor-pointer ${todo.selected ? 'block' : 'hidden'} group-hover:block`}
            onClick={(e) => {
              e.stopPropagation();
              handleEditClick(todo);
            }}
          />
          <div
            className="ml-2 h-4 w-4 text-gray-500 hover:text-gray-700 cursor-pointer relative ${todo.selected ? 'block' : 'hidden'} group-hover:block"
            onMouseEnter={handleLinkMouseEnter}
            onMouseLeave={handleLinkMouseLeave}
            onClick={handleLinkClick}
          >
            {isHovered || isClicked ? (
              <LinkSelectedIcon className="h-4 w-4 text-gray-500" />
            ) : (
              <LinkIcon className="h-4 w-4 text-gray-500" />
            )}
          </div>
        </div>
      </div>
      <div className="flex items-center space-x-2 ml-3" style={{ marginRight: isDeleteMode ? '40px' : '0' }}>
        {todo.planDate && (
          <div className="flex items-center gap-1" onClick={(e) => handleDateClick(e, 'plan', todo.id)}>
            <PlannedIcon className="h-5 w-5 cursor-pointer" />
            <span className={`font-medium text-sm cursor-pointer ${todo.completed ? 'text-gray-600' : 'text-gray-900'}`}>
              {new Date(todo.planDate).toLocaleDateString('de-DE', { day: 'numeric', month: 'short' })}
            </span>
          </div>
        )}
        <div className="flex items-center gap-1" onClick={(e) => handleDateClick(e, 'deadline', todo.id)}>
          {todo.deadlineDate ? (
            <PlanTaskIcon className="h-5 w-5 cursor-pointer" />
          ) : (
            <PlanTaskGreyIcon className="h-5 w-5 cursor-pointer" />
          )}
          {todo.deadlineDate && (
            <span className={`font-medium text-sm cursor-pointer ${todo.completed ? 'text-gray-600' : 'text-gray-900'}`}>
              {new Date(todo.deadlineDate).toLocaleDateString('de-DE', { day: 'numeric', month: 'short' })}
            </span>
          )}
          {showCalendar && calendarPurpose === 'deadline' && selectedTodoId === todo.id && (
            <div className="absolute mt-2">
              <Calendar
                selectedDate={new Date(todo.deadlineDate || new Date())}
                setSelectedDate={handleDateChange}
                setShowCalendar={setShowCalendar}
              />
            </div>
          )}
        </div>
        <div className="pl-2">
          <AvatarGroup
            assignedUsers={todo.assignedUsers}
            users={initialUsers}
            selected={todo.selected}
          />
        </div>
      </div>
      {isDeleteMode && (
        <button
          type="button"
          className="absolute right-2 text-red-500"
          onClick={(e) => {
            e.stopPropagation();
            handleDelete(todo.id);
          }}
        >
          <XIcon className="h-5 w-5" />
        </button>
      )}
      {showCalendar && calendarPurpose === 'plan' && selectedTodoId === todo.id && (
        <div className="absolute mt-2">
          <Calendar
            selectedDate={new Date(todo.planDate || new Date())}
            setSelectedDate={handleDateChange}
            setShowCalendar={setShowCalendar}
          />
        </div>
      )}
    </div>
  );
}
