import React from 'react';
import ToDo from './ToDo';
import { startOfToday, isSameDay, isAfter, isBefore } from 'date-fns';

export default function ToDoList({ todos = [], setTodos, isDeleteMode, onEditTodo, selectedCategory }) {
  const handleCheckboxChange = (id) => {
    const updatedTodos = todos.map(todo => {
      if (todo.taskId === id) {
        return { ...todo, completed: !todo.completed };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const handleDelete = (id) => {
    const updatedTodos = todos.filter(todo => todo.taskId !== id);
    setTodos(updatedTodos);
  };

  const handleTodoClick = (todo, e) => {
    if (e.target.tagName === 'LABEL') {
      onEditTodo(todo);
    } else {
      setTodos(todos.map(t => 
        t.taskId === todo.taskId ? { ...t, selected: !t.selected } : { ...t, selected: false }
      ));
    }
  };

  const handleEditClick = (todo) => {
    onEditTodo(todo);
  };

  // Ensure selectedCategory is defined before accessing name
  const selectedCategoryName = selectedCategory ? selectedCategory.name : '';

  const today = startOfToday();

  const filteredTodos = todos.filter(todo => {
    switch (selectedCategoryName) {
      case 'Planned':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
      case 'Today':
      case 'Personal':
      case 'Work':
      case 'Any Time':
        return (todo.category.name === selectedCategoryName && (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)));
      case 'All Tasks':
        return (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)) && todo.category.name !== 'Planned';
      case 'Logbook':
        return todo.category.name === 'Logbook';
      default:
        return todo.category.name === selectedCategoryName;
    }
  });

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => (
          <ToDo
            key={todo.taskId}
            todo={todo}
            handleCheckboxChange={handleCheckboxChange}
            handleTodoClick={handleTodoClick}
            handleDelete={handleDelete}
            handleEditClick={handleEditClick}
            isDeleteMode={isDeleteMode}
            isTodayCategory={selectedCategoryName === 'Today'}
          />
        ))}
      </div>
    </fieldset>
  );
}
