import React, { useState, useEffect } from 'react';
import { connectWebSocket, disconnectWebSocket, sendWebSocketMessage } from '../websocket';

function HelloWebSocket() {
    const [message, setMessage] = useState('');
    const [response, setResponse] = useState('');

    useEffect(() => {
        // Verbindet sich mit dem WebSocket-Server und richtet die Nachrichtenempfangsfunktion ein
        connectWebSocket((type, msg) => {
            if (type === 'greetings' && msg && msg.content) {
                setResponse(msg.content);
            } else {
                setResponse('No content');
            }
        });

        // Trennt die WebSocket-Verbindung bei der Komponenten-Demontage
        return () => {
            disconnectWebSocket();
        };
    }, []);

    const sendMessage = () => {
        if (message.trim()) {
            sendWebSocketMessage('/app/hello', message);
            setMessage(''); // Leert das Eingabefeld nach dem Senden
        }
    };

    return (
        <div>
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Enter your message"
            />
            <button onClick={sendMessage}>Send</button>
            <div>
                <h3>Response:</h3>
                <p>{response}</p>
            </div>
        </div>
    );
}

export default HelloWebSocket;
