import React, { useState } from 'react';
import { AllTasksIcon, AnyTimeIcon, PersonalIcon, AddIcon, PlannedIcon, TodayIcon, WorkIcon, SparkleIcon, XIcon, DeleteIcon, AllTasksLightIcon, AnyTimeLightIcon, PersonalLightIcon, PlannedLightIcon, TodayLightIcon, WorkLightIcon, SparkleLightIcon, BookIcon, BookLightIcon } from '../icons';
import AddCategoryPopUp from './AddCategoryPopUp';

const initialCategories = [
  { id: 1, name: 'All Tasks', icon: AllTasksIcon, lightIcon: AllTasksLightIcon, color: 'text-gray-400', selectedColor: 'text-white', isSelected: true, fullWidth: false, showDelete: false },
  { id: 2, name: 'Today', icon: TodayIcon, lightIcon: TodayLightIcon, color: 'text-white', selectedColor: 'text-white', isSelected: false, fullWidth: false, showDelete: false },
  { id: 3, name: 'Planned', icon: PlannedIcon, lightIcon: PlannedLightIcon, color: 'text-pink-500', selectedColor: 'text-white', isSelected: false, fullWidth: false, showDelete: false },
  { id: 4, name: 'Any Time', icon: AnyTimeIcon, lightIcon: AnyTimeLightIcon, color: 'text-orange-500', selectedColor: 'text-white', isSelected: false, fullWidth: false, showDelete: false },
  { id: 5, name: 'Work', icon: WorkIcon, lightIcon: WorkLightIcon, color: 'text-red-500', selectedColor: 'text-white', isSelected: false, fullWidth: false, showDelete: false },
  { id: 6, name: 'Personal', icon: PersonalIcon, lightIcon: PersonalLightIcon, color: 'text-blue-500', selectedColor: 'text-white', isSelected: false, fullWidth: false, showDelete: false },
  { id: 7, name: 'Logbook', icon: BookIcon, lightIcon: BookLightIcon, color: 'text-green-500', selectedColor: 'text-white', isSelected: false, fullWidth: true, showDelete: false },
];

const customCategoryIcon = SparkleIcon;
const customCategoryLightIcon = SparkleLightIcon;

export default function Categories({ onSelectCategory }) {
  const [categories, setCategories] = useState(initialCategories);
  const [additionalCategories, setAdditionalCategories] = useState([]);
  const [showDeleteIcons, setShowDeleteIcons] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);

  const selectCategory = (categoryId) => {
    const updatedCategories = categories.map(category => ({
      ...category,
      isSelected: category.id === categoryId
    }));
    const updatedAdditionalCategories = additionalCategories.map(category => ({
      ...category,
      isSelected: category.id === categoryId
    }));

    setCategories(updatedCategories);
    setAdditionalCategories(updatedAdditionalCategories);

    const selectedCategory = [...updatedCategories, ...updatedAdditionalCategories].find(category => category.id === categoryId);
    onSelectCategory(selectedCategory);
  };

  const handleAddCategory = (categoryName) => {
    if (categories.length + additionalCategories.length >= 12) {
      return;
    }

    const newCategoryId = categories.length + additionalCategories.length + 1;

    const newCategory = {
      id: newCategoryId,
      name: categoryName,
      icon: customCategoryIcon,
      lightIcon: customCategoryLightIcon,
      color: 'text-gray-400',
      selectedColor: 'text-white',
      isSelected: false,
      fullWidth: false,
      showDelete: false,
    };

    const updatedAdditionalCategories = [...additionalCategories, newCategory];
    setAdditionalCategories(updatedAdditionalCategories);
    setShowAddCategoryPopUp(false);
  };

  const toggleDeleteIcons = () => {
    const updatedCategories = categories.map(category => ({
      ...category,
      showDelete: !showDeleteIcons
    }));
    const updatedAdditionalCategories = additionalCategories.map(category => ({
      ...category,
      showDelete: !showDeleteIcons
    }));

    setCategories(updatedCategories);
    setAdditionalCategories(updatedAdditionalCategories);

    setShowDeleteIcons(!showDeleteIcons);
  };

  const deleteCategory = (categoryId) => {
    const updatedAdditionalCategories = additionalCategories.filter(cat => cat.id !== categoryId);
    setAdditionalCategories(updatedAdditionalCategories);
  };

  const allCategories = [...categories.slice(0, -1), ...additionalCategories, categories[categories.length - 1]];

  return (
    <div>
      <div className="grid grid-cols-2 gap-4">
        {allCategories.map((category) => {
          const bgColor = category.isSelected ? '#0374FA' : '#0000000D';
          const ringColor = category.isSelected ? '#0374FA26' : '';
          const textColor = category.isSelected ? 'text-white' : 'text-gray-900';

          const IconComponent = category.isSelected ? category.lightIcon : category.icon;

          return (
            <div
              key={category.id}
              className={`relative flex ${category.fullWidth ? 'flex-row' : 'flex-col'} justify-center items-center rounded-lg ${category.isSelected ? 'ring-2 ring-offset-2' : ''} ${category.fullWidth ? 'col-span-2' : ''}`}
              style={{
                minWidth: category.fullWidth ? '100%' : '140px',
                maxWidth: '100%',
                height: category.fullWidth ? '60px' : '100px',
                borderRadius: '20px',
                backgroundColor: bgColor,
                ringColor: ringColor,
                padding: category.fullWidth ? '0 15px' : '15px'
              }}
              onClick={() => selectCategory(category.id)}
            >
              {category.fullWidth ? (
                <>
                  <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                  <span className={`ml-2 text-sm font-medium ${textColor}`}>{category.name}</span>
                </>
              ) : (
                <>
                  <div className="absolute top-2 left-2 p-2">
                    <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                  </div>
                  <span className={`absolute bottom-2 left-2 p-2 text-sm font-medium ${textColor}`}>{category.name}</span>
                </>
              )}
              {category.showDelete && (
                <button className="absolute top-2 right-2 p-2" onClick={(e) => { e.stopPropagation(); deleteCategory(category.id); }}>
                  <XIcon className="h-5 w-5" />
                </button>
              )}
            </div>
          );
        })}
        <div className="col-span-2 flex items-center justify-center mt-3 gap-3">
          <div className={`flex-grow flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer h-[50px] ${isMaxCategories ? 'opacity-50' : ''}`}
            style={{
              borderRadius: '20px',
              backgroundColor: '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
            onClick={() => setShowAddCategoryPopUp(true)}
          >
            <div className="flex items-center space-x-2 w-full justify-center">
              <div className="flex items-center justify-center" style={{ width: '20px', height: '20px' }}>
                <AddIcon className="h-6 w-6 text-gray-400" />
              </div>
              <span className="text-sm font-medium text-gray-900">Add Category</span>
            </div>
          </div>
          <div className="flex-shrink-0 flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer w-[50px] h-[50px]"
            style={{
              borderRadius: '20px',
              backgroundColor: showDeleteIcons ? '#FC5E5E26' : '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
            onClick={toggleDeleteIcons}
          >
            <div className="flex items-center justify-center">
              <DeleteIcon className={`h-5 w-5 `} />
            </div>
          </div>
        </div>
      </div>
      {showAddCategoryPopUp && (
        <AddCategoryPopUp onAddCategory={handleAddCategory} onClose={() => setShowAddCategoryPopUp(false)} />
      )}
    </div>
  );
}
