import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

class WebSocketService {
    constructor() {
        this.stompClient = null;
        this.reconnectInterval = 5000;
        this.onMessageReceived = null;
        this.connectPromise = null;
        this.subscription = null; // Single subscription
    }

    async connect(onMessageReceived, userId) {
        if (!userId) {
            console.error('userId is undefined. Cannot connect to WebSocket.');
            return;
        }

        if (this.stompClient && this.stompClient.connected) {
            console.warn('WebSocket is already connected');
            return;
        }

        this.onMessageReceived = onMessageReceived;

        this.connectPromise = new Promise((resolve, reject) => {
            const token = localStorage.getItem('jwtToken');
            console.log('JWT Token:', token);

            try {
                const socket = new SockJS(SOCKET_URL);
                this.stompClient = Stomp.over(socket);

                this.stompClient.connect(
                    { Authorization: `Bearer ${token}` },
                    () => {
                        console.log('Connected to WebSocket');
                        this.connectPromise = null;
                        this.subscribe(`/topic/user/${userId}/tasks`);
                        resolve();
                    },
                    (error) => {
                        console.error('Error connecting to WebSocket:', error);
                        this.connectPromise = null;
                        this.reconnect(userId);
                        reject(error);
                    }
                );
            } catch (error) {
                console.error('Unexpected error during WebSocket connection:', error);
                this.connectPromise = null;
                reject(error);
            }
        });

        return this.connectPromise;
    }

    subscribe(destination) {
        if (!this.stompClient) {
            console.error('STOMP client is not initialized');
            return;
        }

        if (this.subscription) {
            console.warn(`Already subscribed to ${destination}`);
            return;
        }

        this.subscription = this.stompClient.subscribe(destination, (message) => {
            console.log(`Received message:`, message);
            if (message.body) {
                try {
                    const parsedMessage = JSON.parse(message.body);
                    if (this.onMessageReceived) {
                        this.onMessageReceived('task', parsedMessage);
                    } else {
                        console.warn('No onMessageReceived callback set');
                    }
                } catch (error) {
                    console.error(`Failed to parse message:`, error);
                }
            }
        });
    }

    reconnect(userId) {
        console.log(`Attempting to reconnect in ${this.reconnectInterval / 1000} seconds...`);
        setTimeout(() => this.connect(this.onMessageReceived, userId), this.reconnectInterval);
    }

    disconnect() {
        if (this.stompClient) {
            this.stompClient.disconnect(() => {
                console.log('Disconnected from WebSocket');
                this.stompClient = null;
                this.subscription = null;
            });
        } else {
            console.warn('WebSocket client is not connected');
        }
    }

    sendMessage(destination, message) {
        if (this.stompClient && this.stompClient.connected) {
            console.log('Sending message to:', destination);
            console.log('Message content:', JSON.stringify(message));
            this.stompClient.send(destination, {}, JSON.stringify(message));
        } else {
            console.error('STOMP client is not connected');
        }
    }
}

const webSocketService = new WebSocketService();

export const connectWebSocket = (onMessageReceived, userId) => {
    console.log('Connecting WebSocket with userId:', userId);
    webSocketService.connect(onMessageReceived, userId);
};
export const disconnectWebSocket = () => webSocketService.disconnect();
export const sendWebSocketMessage = (destination, message) => webSocketService.sendMessage(destination, message);
