import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

let stompClient = null;

export const connectWebSocket = (userId, onTaskMessageReceived, onCategoryMessageReceived) => {
  const socket = new SockJS('http://localhost:8080/ws');
  stompClient = new Client({
    webSocketFactory: () => socket,
    reconnectDelay: 5000,
    heartbeatIncoming: 4000,
    heartbeatOutgoing: 4000,
  });

  stompClient.onConnect = (frame) => {
    console.log('WebSocket connected');

    try {
      // Subscribing to user's task and category queues
      stompClient.subscribe(`/user/${userId}/queue/tasks`, (message) => {
        const parsedMessage = JSON.parse(message.body);
        console.log('Task message received for user:', userId, parsedMessage);
        onTaskMessageReceived(parsedMessage);
      });
      console.log(`SUBSCRIBED to task topic: /user/${userId}/queue/tasks`);

      stompClient.subscribe(`/user/${userId}/queue/categories`, (message) => {
        const parsedMessage = JSON.parse(message.body);
        console.log('Category message received for user:', userId, parsedMessage);
        onCategoryMessageReceived(parsedMessage);
      });
      console.log(`SUBSCRIBED to category topic: /user/${userId}/queue/categories`);
    } catch (error) {
      console.error('Subscription error:', error);
    }
  };

  stompClient.onStompError = (frame) => {
    console.error('Broker reported error: ' + frame.headers['message']);
    console.error('Additional details: ' + frame.body);
  };

  stompClient.onWebSocketClose = (event) => {
    console.warn('WebSocket closed:', event);
  };

  stompClient.onWebSocketError = (error) => {
    console.error('WebSocket error:', error);
  };

  stompClient.activate();
};

export const disconnectWebSocket = () => {
  if (stompClient !== null) {
    stompClient.deactivate();
    console.log('WebSocket disconnected');
  }
};

export const sendWebSocketMessage = (destination, message) => {
  if (stompClient && stompClient.connected) {
    stompClient.publish({
      destination: destination,
      body: JSON.stringify(message),
    });
    console.log('Sending WebSocket message:', message);
  } else {
    console.error('WebSocket is not connected');
  }
};
