import React, { useEffect, useState } from 'react';
import { connectWebSocket, disconnectWebSocket, sendWebSocketMessage } from '../websocket';

const WebSocketTestComponent = () => {
  const [messages, setMessages] = useState([]);
  const userId = 1; // Assuming the user ID is 1 for testing purposes

  useEffect(() => {
    const onMessageReceived = (message) => {
      console.log("Message received in component:", message);
      setMessages((prevMessages) => [...prevMessages, message]);
    };

    connectWebSocket(userId, onMessageReceived);

    return () => {
      disconnectWebSocket();
    };
  }, [userId]);

  const sendTestMessage = () => {
    const message = { taskId: 1, text: 'Test Task', userId: 1, categoryId: 1, type: 'TASK_UPDATE', message: 'This is a test message' };
    console.log("Sending test message:", message);
    sendWebSocketMessage(`/app/sendPrivateMessage`, message);
  };

  return (
    <div>
      <h2>WebSocket Test Component</h2>
      <button onClick={sendTestMessage}>Send Test Message</button>
      <ul>
        {messages.map((message, index) => (
          <li key={index}>{JSON.stringify(message)}</li>
        ))}
      </ul>
    </div>
  );
};

export default WebSocketTestComponent;
