import React, { useEffect, useState } from 'react';
import { connectWebSocket, disconnectWebSocket, sendWebSocketMessage } from './webSocketService';

const ToDoList = ({ userId }) => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    connectWebSocket(userId, handleMessageReceived);
    fetchTodos();

    return () => {
      disconnectWebSocket();
    };
  }, [userId]);

  const fetchTodos = () => {
    fetch(`/api/v1/task/getTasks?userId=${userId}`)
      .then(response => response.json())
      .then(data => setTodos(data))
      .catch(error => console.error('Error fetching tasks:', error));
  };

  const handleMessageReceived = (message) => {
    console.log("WebSocket message received:", message);
    if (message.type === 'TASK_UPDATE') {
      updateTaskInState(message.task);
    }
  };

  const updateTaskInState = (updatedTask) => {
    setTodos(prevTodos => {
      const todos = prevTodos.map(task =>
        task.taskId === updatedTask.taskId ? updatedTask : task
      );
      return todos;
    });
  };

  const handleCheckboxChange = (taskId) => {
    const task = todos.find(task => task.taskId === taskId);
    if (task) {
      const updatedTask = { ...task, completed: !task.completed };

      fetch(`/api/v1/task/update`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTask),
      })
        .then(response => response.json())
        .then(data => {
          updateTaskInState(data);
          sendWebSocketMessage('/app/updateTask', { task: data });
        })
        .catch(error => console.error('Error updating task:', error));
    }
  };

  return (
    <div>
      <h1>To-Do List</h1>
      <ul>
        {todos.map(task => (
          <li key={task.taskId}>
            <label>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => handleCheckboxChange(task.taskId)}
              />
              {task.text}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ToDoList;
