import React, { useState } from 'react';
import { AddTodoIcon, ShareLinkIcon, DeleteIcon } from '../icons';

export default function ToDoActions({ toggleAddTaskModal, toggleDeleteMode }) {
  const [deleteModeActive, setDeleteModeActive] = useState(false);
  const [shareText, setShareText] = useState('Share List');
  const [shareTextColor, setShareTextColor] = useState('text-gray-500');

  const handleDeleteModeToggle = () => {
    setDeleteModeActive(!deleteModeActive);
    toggleDeleteMode();
  };

  const handleShareListClick = async () => {
    try {
      // Backend logic to generate link with token
      const response = await fetch('/api/share-category', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ categoryId: 1 }), // Replace with actual category ID
      });

      if (response.ok) {
        const data = await response.json();
        const linkWithToken = data.link; // The actual link returned from the backend

        await navigator.clipboard.writeText(linkWithToken);
        setShareText('Copied.');
        setShareTextColor('text-[#0374FA]');
        setTimeout(() => {
          setShareText('Share List');
          setShareTextColor('text-gray-500');
        }, 1000);
      } else {
        throw new Error('Failed to generate link');
      }
    } catch (err) {
      console.error('Failed to copy: ', err);
      setShareText('Error');
      setShareTextColor('text-red-500');
      setTimeout(() => {
        setShareText('Share List');
        setShareTextColor('text-gray-500');
      }, 1000);
    }
  };

  return (
    <div className="mt-8">
      <hr className="mb-4" style={{ borderColor: '#0000000D' }} />
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4 flex-grow">
          <button
            className="flex items-center justify-center gap-2 h-10 px-4 rounded-lg hover:bg-blue-100"
            style={{ background: 'rgba(3, 116, 250, 0.15)', borderRadius: '15px', width: 'Auto' }}
            onClick={toggleAddTaskModal}
          >
            <AddTodoIcon className="h-5 w-5" />
            <span className="text-blue-500 font-medium hidden sm:block">Add Task</span>
            <span className="text-blue-500 font-medium block sm:hidden">Add</span>
          </button>
          <button
            className="flex items-center justify-center gap-2 h-10 px-4 rounded-lg hover:bg-gray-200"
            style={{ background: 'rgba(0, 0, 0, 0.05)', borderRadius: '15px', width: 'Auto' }}
            onClick={handleShareListClick}
          >
            <ShareLinkIcon className={`h-5 w-5 ${shareTextColor}`} />
            <span className={`font-medium ${shareTextColor} hidden sm:block`}>{shareText}</span>
            <span className={`font-medium ${shareTextColor} block sm:hidden`}>Share</span>
          </button>
        </div>
        <button
          className={`flex items-center justify-center h-10 w-10 rounded-lg ml-4 ${deleteModeActive ? 'bg-red-100 hover:bg-red-200' : 'hover:bg-gray-200'}`}
          style={{ background: deleteModeActive ? 'rgba(252, 94, 94, 0.15)' : 'rgba(0, 0, 0, 0.05)', borderRadius: '15px' }}
          onClick={handleDeleteModeToggle}
        >
          <DeleteIcon className={`h-5 w-5 ${deleteModeActive ? 'text-red-500' : 'text-gray-500'}`} />
        </button>
      </div>
    </div>
  );
}
