export const STANDARD_CATEGORIES = [
  { id: 1, name: 'All Tasks' },
  { id: 2, name: 'Today' },
  { id: 3, name: 'Planned' },
  { id: 4, name: 'Any Time' },
  { id: 5, name: 'Work' },
  { id: 6, name: 'Personal' },
  { id: 7, name: 'Logbook' },


];
export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  // Initialize categorizedTasks for each category
  categories.forEach(category => {
    categorizedTasks[category.categoryId] = [];
  });

  // Assign tasks to their respective categories
  tasks.forEach(task => {
    if (categorizedTasks[task.categoryId]) {
      categorizedTasks[task.categoryId].push(task);
    }
  });

  // Handle special categories
  const specialCategories = {
    1: tasks.filter(task => task.categoryId !== 7), // All Tasks (excluding Logbook)
    2: tasks.filter(task => task.categoryId === 2), // Today
    3: tasks.filter(task => task.planDate), // Planned
    4: tasks.filter(task => task.categoryId === 4), // Any Time
    5: tasks.filter(task => task.categoryId === 5), // Work
    6: tasks.filter(task => task.categoryId === 6), // Personal
    7: tasks.filter(task => task.categoryId === 7) // Logbook
  };

  // Merge special categories with the categorizedTasks
  Object.keys(specialCategories).forEach(id => {
    categorizedTasks[id] = specialCategories[id];
  });

  return categorizedTasks;
};
