import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

class WebSocketService {
  constructor() {
    this.stompClient = null;
    this.connected = false;
    this.reconnectDelay = 5000; // 5 Sekunden
    this.subscriptions = {}; // Abonnements speichern, um doppelte Listener zu vermeiden
  }

  connect(userId, onTaskMessageReceived, onCategoryMessageReceived, onNotifyMessageReceived) {
    if (this.connected) {
      console.log('Bereits mit WebSocket verbunden');
      return;
    }

    console.log('Versuche, WebSocket zu verbinden...');
    const socket = new SockJS(SOCKET_URL);
    this.stompClient = Stomp.over(socket);

    // Verbindung zum WebSocket herstellen
    this.stompClient.connect({}, (frame) => {
      console.log('Mit WebSocket verbunden:', frame);
      this.connected = true;
      this.subscribeToTopics(userId, onTaskMessageReceived, onCategoryMessageReceived, onNotifyMessageReceived);
    }, (error) => {
      console.error('Fehler beim Verbinden mit WebSocket:', error);
      this.connected = false;
      setTimeout(() => this.connect(userId, onTaskMessageReceived, onCategoryMessageReceived, onNotifyMessageReceived), this.reconnectDelay);
    });
  }

  subscribeToTopics(userId, onTaskMessageReceived, onCategoryMessageReceived, onNotifyMessageReceived) {
    // Abonnieren der verschiedenen Topics
    this.subscribe(`/user/${userId}/topic/tasks`, onTaskMessageReceived, `tasks-${userId}`);
    this.subscribe(`/user/${userId}/topic/categories`, onCategoryMessageReceived, `categories-${userId}`);
    this.subscribe(`/user/${userId}/queue/notify`, onNotifyMessageReceived, `notify-${userId}`);
  }

  subscribe(destination, callback, subscriptionKey) {
    // Sicherstellen, dass wir nicht doppelt abonnieren
    if (!this.subscriptions[subscriptionKey]) {
      console.log(`Abonniere ${subscriptionKey} unter ${destination}`);
      this.subscriptions[subscriptionKey] = this.stompClient.subscribe(destination, (message) => {
        console.log(`${subscriptionKey} Nachricht erhalten:`, message.body);
        try {
          const parsedMessage = JSON.parse(message.body);
          callback(parsedMessage);
        } catch (error) {
          console.error(`Fehler beim Parsen der ${subscriptionKey} Nachricht:`, error);
        }
      });
    }
  }

  disconnect() {
    // WebSocket-Verbindung trennen
    if (this.stompClient) {
      this.stompClient.disconnect(() => {
        console.log('Von WebSocket getrennt');
        this.connected = false;
      });
    } else {
      console.warn('WebSocket-Client ist nicht vorhanden. Keine Verbindung zum Trennen.');
    }
  }

  sendMessage(destination, message) {
    // Nachricht über WebSocket senden
    if (this.connected) {
      this.stompClient.send(destination, {}, JSON.stringify(message));
      console.log('WebSocket-Nachricht gesendet:', { destination, message });
    } else {
      console.error('Nachricht konnte nicht gesendet werden. WebSocket-Verbindung ist nicht hergestellt.');
    }
  }

  sendWebSocketMessageToUser(userId, message) {
    const destination = `/user/${userId}/topic/tasks`;
    this.sendMessage(destination, message);
  }
}

const webSocketService = new WebSocketService();

export const connectWebSocket = (userId, onTaskMessageReceived, onCategoryMessageReceived, onNotifyMessageReceived) => {
  webSocketService.connect(userId, onTaskMessageReceived, onCategoryMessageReceived, onNotifyMessageReceived);
};

export const disconnectWebSocket = () => {
  console.log('Trenne WebSocket-Verbindung...');
  webSocketService.disconnect();
};

export const sendWebSocketMessage = (destination, message) => {
  console.log('Sende WebSocket-Nachricht...');
  webSocketService.sendMessage(destination, message);
};

export const sendWebSocketMessageToUser = (userId, message) => {
  webSocketService.sendWebSocketMessageToUser(userId, message);
};
