export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  categories.forEach(category => {
    categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
  });

  return categorizedTasks;
};

export const STANDARD_CATEGORIES = [
  { id: 'all-tasks', name: 'All Tasks', icon: AllTasksIcon },
  { id: 'today', name: 'Today', icon: TodayIcon },
  { id: 'planned', name: 'Planned', icon: PlannedIcon },
  { id: 'any-time', name: 'Any Time', icon: AnyTimeIcon },
  { id: 'work', name: 'Work', icon: WorkIcon },
  { id: 'personal', name: 'Personal', icon: PersonalIcon },
  { id: 'logbook', name: 'Logbook', icon: BookIcon },
];
