import axios from 'axios';
import Cookies from 'js-cookie';

const api = axios.create({
  baseURL: 'http://localhost:8080/api/v1',
  withCredentials: true,
});

// Tasks
export const fetchTasks = (userId) => api.get(`/task/getTasks?userId=${userId}`, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

export const addTask = (task) => api.post('/task/save', task, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

export const updateTask = (task) => api.put('/task/update', task, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

export const deleteTask = (id) => api.delete(`/task/delete/${id}`, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

// Users
export const fetchUser = (userId) => api.get(`/user/${userId}`, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

export const registerUser = (user) => api.post('/user/register', user).then(res => res.data);

export const loginUser = async (credentials) => {
  try {
    console.log('Attempting to login with credentials:', credentials);
    const response = await api.post('/user/login', credentials);
    console.log('Login response:', response);

    const token = response.headers['authorization'];
    console.log('Storing token:', token);
    if (token) {
      Cookies.set('token', token, { expires: 1 });
    } else {
      console.error('No token found in response headers');
    }

    return response.data;
  } catch (error) {
    console.error('Error during login:', error);
    throw error;
  }
};

export const fetchCurrentUser = async () => {
  const token = Cookies.get('token');
  console.log('Fetching current user with token:', token);
  const response = await api.get('/user/me', {
    headers: {
      Authorization: token,
    },
  });
  console.log('Current user:', response.data);
  return response.data;
};

export const updateUser = (user) => api.put('/user/update', user, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

export const deleteUser = (id) => api.delete(`/user/deleteuser/${id}`, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

// Categories
export const addCategory = (category) => api.post('/category/save', category, {
  headers: {
    Authorization: `Bearer ${Cookies.get('token')}`,
  }
}).then(res => res.data);

// Tokens
export const generateTokenForCategory = async (categoryId) => {
  try {
    const response = await api.post('/token/generateForCategory', { categoryId }, {
      headers: {
        Authorization: `Bearer ${Cookies.get('token')}`,
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error generating token for category:', error);
    throw error;
  }
};

export const generateTokenForTask = async (taskId) => {
  try {
    const response = await api.post('/token/generateForTask', { taskId }, {
      headers: {
        Authorization: `Bearer ${Cookies.get('token')}`,
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error generating token for task:', error);
    throw error;
  }
};
