import React, { useState } from 'react';
import ToDo from './ToDo';
import { startOfToday, isSameDay, isAfter, isBefore } from 'date-fns';

export default function ToDoList({ todos = [], setTodos, isDeleteMode, onEditTodo, onDateChange, selectedCategory }) {
  const [selectedTodoId, setSelectedTodoId] = useState(null);

  const handleCheckboxChange = (id) => {
    const updatedTodos = todos.map(todo => 
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    );
    setTodos(updatedTodos);
  };

  const handleDelete = (id) => {
    const updatedTodos = todos.filter(todo => todo.id !== id);
    setTodos(updatedTodos);
  };

  const handleTodoClick = (id) => {
    setSelectedTodoId(id === selectedTodoId ? null : id); // Toggle selection
  };

  const handleEditClick = (todo) => {
    onEditTodo(todo);
  };

  const handleDateChange = async (id, date, purpose) => {
    try {
      const updatedTodos = todos.map(todo =>
        todo.id === id ? { ...todo, [`${purpose}Date`]: date } : todo
      );
      await onDateChange(id, date, purpose);
    } catch (error) {
      console.error('Error updating date:', error);
    }
  };

  const today = startOfToday();

  const filteredTodos = todos.filter(todo => {
    switch (selectedCategory.name) {
      case 'Planned':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
      case 'Today':
      case 'Personal':
      case 'Work':
      case 'Any Time':
        return (todo.category === selectedCategory.name && (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)));
      case 'All Tasks':
        return (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)) && todo.category !== 'Planned';
      case 'Logbook':
        return todo.category === 'Logbook';
      default:
        return todo.category === selectedCategory.name;
    }
  });

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => (
          <ToDo
            key={todo.id}
            todo={todo}
            isSelected={todo.id === selectedTodoId}
            handleCheckboxChange={() => handleCheckboxChange(todo.id)}
            handleTodoClick={() => handleTodoClick(todo.id)}
            handleDelete={() => handleDelete(todo.id)}
            handleEditClick={() => handleEditClick(todo)}
            isDeleteMode={isDeleteMode}
            onDateChange={handleDateChange}
            isTodayCategory={selectedCategory.name === 'Today'}
          />
        ))}
      </div>
    </fieldset>
  );
}
