import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

class WebSocketService {
  constructor() {
    this.stompClient = null;
    this.connected = false;
    this.reconnectDelay = 5000; // 5 Sekunden
    this.subscriptions = {}; // Abonnements speichern, um doppelte Listener zu vermeiden
    this.messageQueue = []; // Warteschlange für Nachrichten, die gesendet werden sollen, bevor die Verbindung hergestellt ist
  }

  connect(userId, onTaskMessageReceived, onCategoryMessageReceived) {
    if (this.connected) {
        console.log('Already connected to WebSocket');
        return;
    }

    console.log('Attempting to connect to WebSocket...');
    const socket = new SockJS(SOCKET_URL);
    this.stompClient = Stomp.over(socket);

    this.stompClient.connect({}, (frame) => {
        console.log('Connected to WebSocket:', frame);
        this.connected = true;
        this.subscribeToTopics(userId, onTaskMessageReceived, onCategoryMessageReceived);

        // Sende alle Nachrichten in der Warteschlange
        while (this.messageQueue.length > 0) {
            const { destination, message } = this.messageQueue.shift();
            this.sendMessage(destination, message);
        }
    }, (error) => {
        console.error('Error connecting to WebSocket:', error);
        this.connected = false;
        setTimeout(() => this.connect(userId, onTaskMessageReceived, onCategoryMessageReceived), this.reconnectDelay);
    });
}


  subscribeToTopics(userId, onTaskMessageReceived, onCategoryMessageReceived) {
    this.subscribe(`/user/${userId}/queue/tasks`, onTaskMessageReceived, `tasks-${userId}`);
    this.subscribe(`/user/${userId}/queue/categories`, onCategoryMessageReceived, `categories-${userId}`);
  }

  subscribe(destination, callback, subscriptionKey) {
    if (!this.subscriptions[subscriptionKey]) {
      console.log(`Subscribing to ${subscriptionKey} at ${destination}`);
      this.subscriptions[subscriptionKey] = this.stompClient.subscribe(destination, (message) => {
        console.log(`${subscriptionKey} message received:`, message.body);
        try {
          const parsedMessage = JSON.parse(message.body);
          console.log('Parsed message:', parsedMessage); // Debugging log
          callback(parsedMessage);
        } catch (error) {
          console.error(`Error parsing ${subscriptionKey} message:`, error);
        }
      });
    }
  }

  disconnect() {
    if (this.stompClient) {
      this.stompClient.disconnect(() => {
        console.log('Disconnected from WebSocket');
        this.connected = false;
      });
    } else {
      console.warn('WebSocket client not present. No connection to disconnect.');
    }
  }

  sendMessage(destination, message) {
    if (this.connected) {
      this.stompClient.send(destination, {}, JSON.stringify(message));
      console.log('WebSocket message sent:', { destination, message });
    } else {
      console.error('Message could not be sent. WebSocket connection is not established.');
      this.messageQueue.push({ destination, message }); // Nachricht in die Warteschlange stellen
    }
  }

  sendWebSocketMessageToUser(userId, message) {
    const destination = `/user/${userId}/queue/tasks`;
    this.sendMessage(destination, message);
  }
}

const webSocketService = new WebSocketService();

export const connectWebSocket = (userId, onTaskMessageReceived, onCategoryMessageReceived) => {
  webSocketService.connect(userId, onTaskMessageReceived, onCategoryMessageReceived);
};

export const disconnectWebSocket = () => {
  console.log('Disconnecting WebSocket...');
  webSocketService.disconnect();
};

export const sendWebSocketMessage = (destination, message) => {
  console.log('Sending WebSocket message...');
  webSocketService.sendMessage(destination, message);
};

export const sendWebSocketMessageToUser = (userId, message) => {
  webSocketService.sendWebSocketMessageToUser(userId, message);
};
