export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  // Initialize categorizedTasks for each category
  categories.forEach(category => {
    categorizedTasks[category.categoryId] = [];
  });

  // Initialize standard categories
  const standardCategories = [1, 2, 3, 4, 5, 6, 7];
  standardCategories.forEach(id => {
    if (!categorizedTasks[id]) {
      categorizedTasks[id] = [];
    }
  });

  // Lists to hold tasks based on conditions
  const allTasks = [];
  const logbookTasks = [];
  const todayTasks = [];
  const plannedTasks = [];
  const anyTimeTasks = [];
  const workTasks = [];
  const personalTasks = [];

  // Categorize tasks into different lists
  tasks.forEach(task => {
    const { categoryId, completed, planDate } = task;

    // Debugging: Log task information
    console.log(`Processing task with ID: ${task.taskId}, categoryId: ${categoryId}`);

    // Always add task to its specific category
    if (!categorizedTasks[categoryId]) {
      categorizedTasks[categoryId] = [];
    }
    categorizedTasks[categoryId].push(task);

    // Add tasks to standard categories based on conditions
    if (categoryId !== 7) {
      allTasks.push(task); // All Tasks (excluding Logbook)
    }
    if (completed) {
      logbookTasks.push(task); // Logbook
    } else {
      if (categoryId === 2) {
        todayTasks.push(task); // Today
      }
      if (planDate) {
        plannedTasks.push(task); // Planned
      }
      if (categoryId === 4) {
        anyTimeTasks.push(task); // Any Time
      }
      if (categoryId === 5) {
        workTasks.push(task); // Work
      }
      if (categoryId === 6) {
        personalTasks.push(task); // Personal
      }
    }
  });

  // Add lists to categorizedTasks object
  categorizedTasks[1] = allTasks;
  categorizedTasks[7] = logbookTasks;
  categorizedTasks[2] = todayTasks;
  categorizedTasks[3] = plannedTasks;
  categorizedTasks[4] = anyTimeTasks;
  categorizedTasks[5] = workTasks;
  categorizedTasks[6] = personalTasks;

  // Debugging: Log categorized tasks
  console.log('Categorized tasks:', categorizedTasks);

  return categorizedTasks;
};
