import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8080/api',
  withCredentials: true,
});

export const fetchTodos = (category) => api.get(`/todos?category=${category}`).then(res => res.data);

export const addTask = (task) => api.post('/add-task', task).then(res => res.data);

export const updateTask = (id, task) => api.put(`/update-task/${id}`, task).then(res => res.data);

export const deleteTask = (id) => api.delete(`/delete-task/${id}`).then(res => res.data);

export const setTaskDate = (id, dateRequest) => api.put(`/set-task-date/${id}`, dateRequest).then(res => res.data);

export const fetchUser = () => api.get('/users/current').then(res => res.data);
