import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

let stompClient = null;

const connectWebSocket = (userId, onMessageReceived) => {
  const socket = new SockJS(SOCKET_URL);
  stompClient = Stomp.over(socket);

  const token = localStorage.getItem('jwtToken');
  const headers = token ? { Authorization: `Bearer ${token}` } : {};

  stompClient.connect(headers, () => {
    console.log('Connected to WebSocket');

    // Subscribe to user-specific queue for tasks
    stompClient.subscribe(`/user/${userId}/queue/tasks`, (message) => {
      console.log("Received message on /user/" + userId + "/queue/tasks:", message);
      handleWebSocketMessage(message, onMessageReceived);
    });
    stompClient.subscribe('/topic/tasks', (message) => {
      console.log("Received message on /topic/tasks:", message);
      handleWebSocketMessage(message, onMessageReceived);
    });

  }, (error) => {
    console.error('Error connecting to WebSocket:', error);
    setTimeout(() => connectWebSocket(userId, onMessageReceived), 5000);
  });
};

const handleWebSocketMessage = (message, onMessageReceived) => {
  if (message.body) {
    try {
      const parsedMessage = JSON.parse(message.body);
      onMessageReceived(parsedMessage);
    } catch (error) {
      console.error('Failed to parse WebSocket message:', error);
    }
  } else {
    console.error('Message body is missing or undefined');
  }
};

const disconnectWebSocket = () => {
  if (stompClient) {
    stompClient.disconnect(() => {
      console.log('Disconnected from WebSocket');
    });
  }
};

const sendWebSocketMessage = (destination, message) => {
  if (stompClient && stompClient.connected) {
    const token = localStorage.getItem('jwtToken');
    const headers = token ? { Authorization: `Bearer ${token}` } : {};
    console.log('Sending message to', destination);
    try {
      stompClient.send(destination, headers, JSON.stringify(message));
    } catch (error) {
      console.error('Failed to send WebSocket message:', error);
    }
  } else {
    console.error('WebSocket is not connected');
  }
};

const sendUpdateTaskMessage = (taskDTO) => {
  console.log('Sending update task message:', taskDTO);
  sendWebSocketMessage(`/app/updateTask/${taskDTO.taskId}`, { ...taskDTO, type: 'TASK_UPDATE' });
};

const sendTestMessage = (message) => {
  console.log('Sending test message:', message);
  sendWebSocketMessage(`/app/sendPrivateMessage`, message);
};

export { connectWebSocket, disconnectWebSocket, sendWebSocketMessage, sendUpdateTaskMessage, sendTestMessage };
