import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8080/api/v1',
  withCredentials: true,
});
// api.js
export const loginUser = (credentials) => {
  return axios.post('/api/v1/user/login', credentials)
    .then(response => {
      if (response.headers.authorization) {
        localStorage.setItem('token', response.headers.authorization);
      }
      return response.data;
    })
    .catch(error => {
      console.error('Error during login:', error);
      throw error;
    });
};

export const fetchCurrentUser = () => {
  const token = localStorage.getItem('token');
  return axios.get('/api/v1/user/me', {
    headers: {
      Authorization: token
    }
  })
  .then(response => response.data)
  .catch(error => {
    console.error('Error fetching current user:', error);
    throw error;
  });
};
// Tasks
export const fetchTasks = () => api.get('/task/getTasks').then(res => res.data);

export const addTask = (task) => api.post('/task/save', task).then(res => res.data);

export const updateTask = (task) => api.put('/task/update', task).then(res => res.data);

export const deleteTask = (id) => api.delete(`/task/delete/${id}`).then(res => res.data);

// Users
export const fetchUser = (userId) => api.get(`/user/${userId}`).then(res => res.data);

export const registerUser = (user) => api.post('/user/register', user).then(res => res.data);


export const updateUser = (user) => api.put('/user/update', user).then(res => res.data);

export const deleteUser = (id) => api.delete(`/user/deleteuser/${id}`).then(res => res.data);

// Categories
export const addCategory = (category) => api.post('/category/save', category).then(res => res.data);

// Tokens
export const generateTokenForCategory = async (categoryId) => {
  try {
    const response = await api.post('/token/generateForCategory', { categoryId });
    return response.data;
  } catch (error) {
    console.error('Error generating token for category:', error);
    throw error;
  }
};

export const generateTokenForTask = async (taskId) => {
  try {
    const response = await api.post('/token/generateForTask', { taskId });
    return response.data;
  } catch (error) {
    console.error('Error generating token for task:', error);
    throw error;
  }
};
