import React, { useState, useEffect, useCallback } from 'react';
import ToDo from './ToDo';
import { sendWebSocketMessage } from '../websocket';
import { updateTask } from '../api';
import { startOfToday, isAfter } from 'date-fns';

export default function ToDoList({ todos = [], setTasks, isDeleteMode, onEditTodo, selectedCategory, handleDeleteTask, userId }) {
  const [selectedTodoId, setSelectedTodoId] = useState(null);

  useEffect(() => {
    console.log('Todos updated:', todos);
  }, [todos]);

  const handleCheckboxChange = async (id) => {
    console.log('Checkbox changed for ID:', id);
    const updatedTodos = todos.map(todo => {
      if (todo.taskId === id) {
        const updatedTodo = { ...todo, completed: !todo.completed };
        if (updatedTodo.completed) {
          updatedTodo.categoryId = 7; 
        }
        return updatedTodo;
      }
      return todo;
    });

    setTasks(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === id);
    try {
      await updateTask(updatedTask, userId);
      sendWebSocketMessage('/user/' + userId + '/topic/tasks', { type: 'TASK_UPDATE', task: updatedTask });
      console.log('WebSocket update task successful');
    } catch (error) {
      console.error('Error updating task via WebSocket:', error);
    }
  };

  const handleDeleteClick = async (id) => {
    console.log('Deleting task with ID:', id);
    try {
      await handleDeleteTask(id);
      setTasks(prevTasks => prevTasks.filter(task => task.taskId !== id));
    } catch (error) {
      console.error('Error deleting task:', error.response ? error.response.data : error.message);
    }
  };

  const handleDateChange = async (id, date, purpose) => {
    console.log(`Updating ${purpose} date for ID: ${id}`);
    const updatedTodos = todos.map(todo => {
      if (todo.taskId === id) {
        return {
          ...todo,
          [`${purpose}Date`]: date ? new Date(date).toISOString() : null,
        };
      }
      return todo;
    });

    setTasks(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === id);
    try {
      sendWebSocketMessage('/user/' + userId + '/topic/tasks', { type: 'TASK_UPDATE', task: updatedTask });
      console.log('WebSocket update date successful');
    } catch (error) {
      console.error('Error updating date via WebSocket:', error);
    }
  };

  const today = startOfToday();

  const filteredTodos = todos.filter(todo => {
    switch (selectedCategory?.name) {
      case 'Planned':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
      case 'Today':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today) && todo.categoryId !== 7;
      case 'Personal':
      case 'Work':
      case 'Any Time':
        return todo.categoryId === selectedCategory?.id;
      case 'All Tasks':
        return !todo.planDate && todo.categoryId !== 7;
      case 'Logbook':
        return todo.categoryId === 7;
      default:
        return todo.categoryId === selectedCategory?.id;
    }
  });

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => {
          if (!todo.taskId) {
            console.error('Todo without taskId:', todo);
          }
          return (
            <ToDo
              key={todo.taskId || Math.random().toString()} // Fallback für fehlende taskId
              todo={todo}
              handleCheckboxChange={() => handleCheckboxChange(todo.taskId)}
              handleTodoClick={() => setSelectedTodoId(todo.taskId === selectedTodoId ? null : todo.taskId)}
              handleDelete={() => handleDeleteClick(todo.taskId)}
              handleEditClick={() => onEditTodo(todo)}
              isSelected={todo.taskId === selectedTodoId}
              onDateChange={handleDateChange}
              isDeleteMode={isDeleteMode}
              userId={userId}
            />
          );
        })}
      </div>
    </fieldset>
  );
}
