// Adjusted standard categories to avoid ID collision
export const STANDARD_CATEGORIES = [
  { id: 'std-1', name: 'All Tasks' },
  { id: 'std-2', name: 'Today' },
  { id: 'std-3', name: 'Planned' },
  { id: 'std-4', name: 'Any Time' },
  { id: 'std-5', name: 'Work' },
  { id: 'std-6', name: 'Personal' },
  { id: 'std-7', name: 'Logbook' },
];

export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  // Initialize categorizedTasks for each category
  categories.forEach(category => {
    categorizedTasks[category.categoryId] = [];
  });

  // Add standard categories
  STANDARD_CATEGORIES.forEach(category => {
    categorizedTasks[category.id] = [];
  });

  // Filter tasks based on category rules
  tasks.forEach(task => {
    const { categoryId, completed, planDate } = task;

    // Always add task to its specific category
    if (!categorizedTasks[categoryId]) {
      categorizedTasks[categoryId] = [];
    }
    categorizedTasks[categoryId].push(task);

    // Add task to standard categories based on rules
    if (categoryId !== 'std-7') {
      categorizedTasks['std-1'].push(task); // All Tasks (excluding Logbook)
    }

    if (completed) {
      categorizedTasks['std-7'].push(task); // Logbook
    } else {
      if (categoryId === 'std-2') {
        categorizedTasks['std-2'].push(task); // Today
      }
      if (planDate) {
        categorizedTasks['std-3'].push(task); // Planned
      }
      if (categoryId === 'std-4') {
        categorizedTasks['std-4'].push(task); // Any Time
      }
      if (categoryId === 'std-5') {
        categorizedTasks['std-5'].push(task); // Work
      }
      if (categoryId === 'std-6') {
        categorizedTasks['std-6'].push(task); // Personal
      }
    }
  });

  return categorizedTasks;
};
