import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp';
import {
  AllTasksIcon,
  AnyTimeIcon,
  PersonalIcon,
  PlannedIcon,
  SparkleIcon,
  SparkleLightIcon,
  TodayIcon,
  WorkIcon,
  BookIcon
} from '../icons';
import { fetchTasks, addTask, updateTask, deleteTask, fetchUserId, fetchEmail, addCategory, fetchCategories } from '../api';
import { connectWebSocket, sendWebSocketMessage, disconnectWebSocket } from '../websocket';

const iconMap = {
  'All Tasks': AllTasksIcon,
  'Today': TodayIcon,
  'Planned': PlannedIcon,
  'Any Time': AnyTimeIcon,
  'Work': WorkIcon,
  'Personal': PersonalIcon,
  'Logbook': BookIcon,
};

const customCategoryIcon = SparkleIcon;
const customCategoryLightIcon = SparkleLightIcon;

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();
  const webSocketRef = useRef(null);

  const initializeWebSocket = useCallback((userId) => {
    if (!webSocketRef.current) {
      console.log('Connecting WebSocket with userId:', userId);
      connectWebSocket(onTaskMessageReceived, onCategoryMessageReceived, userId);
      webSocketRef.current = userId; // Use userId to track connection
    } else {
      console.log('WebSocket already connected for userId:', userId);
    }
  }, []);

  useEffect(() => {
    const fetchUserAndTasksAndCategories = async () => {
      try {
        console.log('Fetching user email...');
        const email = await fetchEmail();
        console.log('Fetched email:', email);

        console.log('Fetching user ID for email:', email);
        const fetchedUserId = await fetchUserId(email);
        if (fetchedUserId) {
          console.log('Fetched userId:', fetchedUserId);
          setUserId(fetchedUserId);

          console.log('Fetching tasks...');
          const tasks = await fetchTasks();
          console.log('Fetched tasks:', tasks);
          setTasks(tasks);

          console.log('Fetching categories for userId:', fetchedUserId);
          const fetchedCategories = await fetchCategories(fetchedUserId);
          console.log('Fetched categories:', fetchedCategories);

          const mappedCategories = fetchedCategories.map(category => ({
            ...category,
            id: category.categoryId,
            icon: iconMap[category.name] || customCategoryIcon,
            isSelected: false,
            fullWidth: false,
            showDelete: false,
          }));
          setCategories(mappedCategories);
          if (mappedCategories.length > 0) {
            setSelectedCategory(mappedCategories[0]);
          }
        } else {
          console.warn('User not found, redirecting to login...');
          navigate('/login');
        }
      } catch (error) {
        console.error('Error fetching user, tasks, or categories:', error);
        navigate('/login');
      }
    };

    fetchUserAndTasksAndCategories();
  }, [navigate]);


  useEffect(() => {
    if (userId) {
      initializeWebSocket(userId);
    }

    // Cleanup function to disconnect WebSocket when component unmounts
    return () => {
      if (webSocketRef.current) {
        disconnectWebSocket();
        webSocketRef.current = null;
      }
    };
  }, [userId, initializeWebSocket]);

  const handleAddNewTask = async (newTask) => {
    try {
      console.log('Adding new task:', newTask);
      console.log('User ID:', userId);

      const formattedTask = {
        ...newTask,
        categoryId: Number(newTask.categoryId),
      };

      const addedTask = await addTask(formattedTask, userId);
      setShowAddTaskPopUp(false);

      if (webSocketRef.current) {
        sendWebSocketMessage(webSocketRef.current, `/user/${userId}/topic/tasks`, { type: 'TASK_ADD', task: addedTask });
        console.log('WebSocket message sent: ', { type: 'TASK_ADD', task: addedTask });
      } else {
        console.error('WebSocket connection is not initialized.');
      }

      console.log('Task added successfully:', addedTask);

      setTasks(prevTasks => {
        const taskExists = prevTasks.some(task => task.taskId === addedTask.taskId);
        if (!taskExists) {
          return [...prevTasks, addedTask];
        }
        return prevTasks;
      });
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };


  const handleSaveTask = async (updatedTask) => {
    try {
      const taskToUpdate = {
        ...updatedTask,
        userId: userId,
        taskId: currentTask.taskId,
      };
      console.log('Updating task:', taskToUpdate);
      const updated = await updateTask(taskToUpdate, userId);
      setShowEditTaskPopUp(false);
      sendWebSocketMessage(`/user/${userId}/topic/tasks`, { type: 'TASK_UPDATE', task: updated });
      console.log('WebSocket message sent: ', { type: 'TASK_UPDATE', task: updated });
      console.log('Task updated successfully:', updated);

      setTasks(prevTasks =>
        prevTasks.map(task => (task.taskId === updated.taskId ? updated : task))
      );
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      console.log('Deleting task with ID:', id);
      await deleteTask(id, userId);
      sendWebSocketMessage(`/user/${userId}/topic/tasks`, { type: 'TASK_DELETE', taskId: id });
      console.log('WebSocket message sent: ', { type: 'TASK_DELETE', taskId: id });
      console.log('Task deleted successfully:', id);
      setTasks(prevTasks => prevTasks.filter(task => task.taskId !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleAddCategory = async (newCategory) => {
    try {
      console.log('Adding new category:', newCategory);

      const categoryExists = categories.some(cat => cat.name === newCategory.name);
      if (categoryExists) {
        console.warn('Category already exists:', newCategory);
        return;
      }

      const data = await addCategory(newCategory);
      console.log('Category added successfully:', data);

      const newCategoryObject = {
        id: data.categoryId,
        name: data.name,
        icon: customCategoryIcon,
        isSelected: false,
        fullWidth: false,
        showDelete: false,
        userId: data.userId,
      };

      setCategories(prevCategories => [...prevCategories, newCategoryObject]);
      sendWebSocketMessage(`/user/${userId}/topic/categories`, { type: 'CATEGORY_ADD', category: newCategoryObject });
      console.log('WebSocket message sent: ', { type: 'CATEGORY_ADD', category: newCategoryObject });
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(prevMode => !prevMode);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true);
  };

  const onTaskMessageReceived = (message) => {
  console.log('WebSocket task message received:', message);
  const { type, task, taskId } = message;

  if (type === 'TASK_ADD' || type === 'TASK_UPDATE') {
    if (!task) {
      console.warn('Invalid task received:', task);
      return;
    }

    console.log('Updating tasks list with:', task);
    setTasks(prevTasks => {
      const taskExists = prevTasks.some(t => t.taskId === task.taskId);
      if (type === 'TASK_ADD' && !taskExists) {
        return [...prevTasks, task];
      } else if (type === 'TASK_UPDATE') {
        return prevTasks.map(t => (t.taskId === task.taskId ? task : t));
      }
      return prevTasks;
    });
  } else if (type === 'TASK_DELETE') {
    if (!taskId) {
      console.warn('Invalid taskId received for delete:', taskId);
      return;
    }
    console.log('Removing task with ID:', taskId);
    setTasks(prevTasks => prevTasks.filter(t => t.taskId !== taskId));
  }
};




  const onCategoryMessageReceived = (message) => {
    console.log('WebSocket category message received:', message);
    const { type, category, categoryId } = message;

    if (!type) {
      console.error('Unknown message type:', type);
      return;
    }

    console.log('Received message:', message);

    if (type === 'CATEGORY_ADD' || type === 'CATEGORY_SHARE') {
      if (!category) {
        console.warn('Invalid category received:', category);
        return;
      }

      setCategories(prevCategories => {
        const categoryExists = prevCategories.some(c => c.id === category.categoryId);
        if (!categoryExists) {
          return [...prevCategories, { ...category, id: category.categoryId, icon: iconMap[category.name] || customCategoryIcon, isSelected: false, fullWidth: false, showDelete: false }];
        }
        return prevCategories;
      });
    } else if (type === 'CATEGORY_UPDATE') {
      setCategories(prevCategories =>
        prevCategories.map(c => (c.id === category.categoryId ? { ...category, id: category.categoryId, icon: iconMap[category.name] || customCategoryIcon, isSelected: false, fullWidth: false, showDelete: false } : c))
      );
    } else if (type === 'CATEGORY_DELETE') {
      if (!categoryId) {
        console.warn('Invalid categoryId received for delete:', categoryId);
        return;
      }
      setCategories(prevCategories => prevCategories.filter(c => c.id !== categoryId));
    } else {
      console.error('Unknown message type:', type);
    }
  };

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories onSelectCategory={setSelectedCategory} userId={userId} categoriesFromBackend={categories} />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              {selectedCategory && selectedCategory.icon && <selectedCategory.icon className="h-6 w-6" />}
              <span className="font-bold text-grayCustom-dark">{selectedCategory?.name}</span>
            </div>
            <ToDoList
              todos={tasks}
              setTasks={setTasks}
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTask}
              selectedCategory={selectedCategory}
              handleDeleteTask={handleDeleteTask}
              userId={userId}
            />
            <ToDoActions
              toggleAddTaskModal={() => setShowAddTaskPopUp(true)}
              toggleDeleteMode={toggleDeleteMode}
              selectedCategoryId={selectedCategory?.id}
              toggleAddCategoryModal={() => setShowAddCategoryPopUp(true)}
              userId={userId}
            />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAddTask={handleAddNewTask}
          onClose={() => setShowAddTaskPopUp(false)}
          currentCategoryId={selectedCategory?.id}
        />
      )}
      {showEditTaskPopUp && currentTask && (
        <EditTaskPopUp
          task={currentTask}
          onSaveTask={handleSaveTask}
          onClose={() => setShowEditTaskPopUp(false)}
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
          userId={userId}
        />
      )}
    </div>
  );
}

export default Dashboard;
