export const STANDARD_CATEGORIES = [
  { id: 1, name: 'All Tasks' },
  { id: 2, name: 'Today' },
  { id: 3, name: 'Planned' },
  { id: 4, name: 'Any Time' },
  { id: 5, name: 'Work' },
  { id: 6, name: 'Personal' },
  { id: 7, name: 'Logbook' },


];
export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  const standardCategoryIds = new Set(STANDARD_CATEGORIES.map(category => category.id));

  // Initialize categorizedTasks for each standard category ID
  standardCategoryIds.forEach(id => {
    categorizedTasks[id] = [];
  });

  // Filter tasks based on category rules
  tasks.forEach(task => {
    const categoryId = task.categoryId;

    if (standardCategoryIds.has(categoryId)) {
      switch (categoryId) {
        case 1:
          // All Tasks: Exclude Logbook (id 7)
          categorizedTasks[1] = tasks;
          break;
        case 2:
          // Today: Tasks with category id 2
          categorizedTasks[2] = tasks.filter(t => t.categoryId === 2);
          break;
        case 3:
          // Planned: Tasks with a planDate
          categorizedTasks[3] = tasks.filter(t => t.planDate);
          break;
        case 4:
          // Any Time: Tasks with category id 4
          categorizedTasks[4] = tasks.filter(t => t.categoryId === 4);
          break;
        case 5:
          // Work: Tasks with category id 5
          categorizedTasks[5] = tasks.filter(t => t.categoryId === 5);
          break;
        case 6:
          // Personal: Tasks with category id 6
          categorizedTasks[6] = tasks.filter(t => t.categoryId === 6);
          break;
        case 7:
          // Logbook: Tasks with category id 7
          categorizedTasks[7] = tasks.filter(t => t.categoryId === 7);
          break;
        default:
          // Handle custom categories
          if (!categorizedTasks[categoryId]) {
            categorizedTasks[categoryId] = [];
          }
          categorizedTasks[categoryId].push(task);
          break;
      }
    }
  });

  return categorizedTasks;
};
