import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp'; // Import the AddCategoryPopUp component
import { PlannedIcon } from '../icons';
import { fetchTasks, addTask, updateTask, deleteTask, fetchUserId, fetchEmail, addCategory } from '../api'; // Import addCategory function

const initialCategories = [
  { id: 1, name: 'All Tasks', icon: PlannedIcon },
  { id: 2, name: 'Today', icon: PlannedIcon },
  { id: 3, name: 'Planned', icon: PlannedIcon },
  { id: 4, name: 'Any Time', icon: PlannedIcon },
  { id: 5, name: 'Work', icon: PlannedIcon },
  { id: 6, name: 'Personal', icon: PlannedIcon },
  { id: 7, name: 'Logbook', icon: PlannedIcon },
];

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false); // State for AddCategoryPopUp
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(initialCategories[0]);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserAndTasks = async () => {
      try {
        const email = await fetchEmail();
        console.log('Fetched email:', email); // Debugging-Ausgabe
        const userId = await fetchUserId(email);
        console.log('Fetched user ID:', userId); // Debugging-Ausgabe
        setUserId(userId);

        await fetchTasksData(userId); // Await verwenden, um auf Abschluss zu warten
      } catch (error) {
        console.error('Error fetching user data:', error);
        navigate('/login'); // Bei Fehler zum Login weiterleiten
      }
    };

    fetchUserAndTasks();
  }, [navigate]);

  const fetchTasksData = async (user) => {
    console.log('Fetching tasks for userId:', user);
    try {
      const tasksData = await fetchTasks(user);
      console.log('Received tasks:', tasksData);
      if (Array.isArray(tasksData)) {
        setTasks(tasksData);
      } else {
        console.error('Expected an array but received:', tasksData);
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleAddNewTask = async (newTask) => {
    try {
      newTask.userId = userId; // userId beim Hinzufügen einer neuen Aufgabe setzen
      const data = await addTask(newTask);
      setTasks(prevTasks => [...prevTasks, data]); // Aufgaben aktualisieren
      await fetchTasksData(userId); // Aufgaben nach dem Hinzufügen neu abrufen
      setShowAddTaskPopUp(false); // Pop-up schließen
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleSaveTask = async (updatedTask) => {
    try {
      // User ID und Task ID hinzufügen
      const taskToUpdate = {
        ...updatedTask,
        userId: userId,
        taskId: currentTask.taskId,
      };
      await updateTask(taskToUpdate); // Aufgabe aktualisieren
      await fetchTasksData(userId); // Aufgaben nach der Aktualisierung neu abrufen
      setShowEditTaskPopUp(false); // Pop-up schließen
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleAddNewCategory = async (newCategory) => {
    try {
      newCategory.user = { userId }; // userId beim Hinzufügen einer neuen Kategorie setzen
      const data = await addCategory(newCategory);
      console.log('Added category:', data);
      setShowAddCategoryPopUp(false); // Pop-up schließen
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(prevMode => !prevMode); // Löschmodus umschalten
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true); // Bearbeiten-Pop-up öffnen
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id); // Aufgabe löschen
      await fetchTasksData(userId); // Aufgaben nach dem Löschen neu abrufen
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories categories={initialCategories} onSelectCategory={setSelectedCategory} />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              {selectedCategory.icon && <selectedCategory.icon className="h-6 w-6" />} {/* Dynamically render icon */}
              <span className="font-bold text-grayCustom-dark">{selectedCategory.name}</span>
            </div>
            <ToDoList
              todos={tasks} // Aufgaben an ToDoList übergeben
              setTodos={setTasks} // Funktion zum Setzen der Aufgaben an ToDoList übergeben
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTask} // Bearbeiten-Funktion an ToDoList übergeben
              selectedCategory={selectedCategory} // Ausgewählte Kategorie an ToDoList übergeben
              fetchTasksData={() => fetchTasksData(userId)} // Funktion zum Abrufen von Aufgaben an ToDoList übergeben
            />
            <ToDoActions
              toggleAddTaskModal={() => setShowAddTaskPopUp(true)} // Funktion zum Öffnen des Hinzufügen-Pop-ups an ToDoActions übergeben
              toggleDeleteMode={toggleDeleteMode} // Funktion zum Umschalten des Löschmodus an ToDoActions übergeben
              selectedCategoryId={selectedCategory.id} // ID der ausgewählten Kategorie an ToDoActions übergeben
              toggleAddCategoryModal={() => setShowAddCategoryPopUp(true)} // Funktion zum Öffnen des Hinzufügen-Pop-ups für Kategorien
            />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAddTask={handleAddNewTask} // Funktion zum Hinzufügen einer neuen Aufgabe an AddTaskPopUp übergeben
          onClose={() => setShowAddTaskPopUp(false)} // Funktion zum Schließen des Pop-ups an AddTaskPopUp übergeben
          currentCategoryId={selectedCategory.id} // Aktuelle Kategorie-ID an AddTaskPopUp übergeben, falls benötigt
          creatorUserId={userId} // Benutzer-ID an AddTaskPopUp übergeben
        />
      )}
      {showEditTaskPopUp && currentTask && (
        <EditTaskPopUp
          task={currentTask} // Aktuelle Aufgabe an EditTaskPopUp übergeben
          onSaveTask={handleSaveTask} // Funktion zum Speichern einer Aufgabe an EditTaskPopUp übergeben
          onClose={() => setShowEditTaskPopUp(false)} // Funktion zum Schließen des Pop-ups an EditTaskPopUp übergeben
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          userId={userId} // Benutzer-ID an AddCategoryPopUp übergeben
          onAddCategory={handleAddNewCategory} // Funktion zum Hinzufügen einer Kategorie an AddCategoryPopUp übergeben
          onClose={() => setShowAddCategoryPopUp(false)} // Funktion zum Schließen des Pop-ups an AddCategoryPopUp übergeben
        />
      )}
    </div>
  );
}

export default Dashboard;
