import axios from 'axios';
import Cookies from 'js-cookie';

const api = axios.create({
  baseURL: 'http://localhost:8080/api/v1', // Deine Basis-URL
  headers: {
    'Content-Type': 'application/json',
  },
});

api.interceptors.request.use(
  (config) => {
    const token = Cookies.get('jwtToken'); // Holen Sie den Token aus den Cookies
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export const registerUser = async (user) => {
  try {
    const response = await api.post('/auth/register', user);
    return response.data;
  } catch (error) {
    console.error('Error registering user:', error);
    throw error;
  }
};

export const loginUser = async (credentials) => {
  try {
    const response = await api.post('/auth/login', credentials);
    const token = response.headers['set-cookie'] || response.data.token;
    if (token) {
      // Speichern Sie den JWT-Token als String in den Cookies
      Cookies.set('jwtToken', token, { expires: 1 });
    }
    return response.data;
  } catch (error) {
    console.error('Error logging in:', error);
    throw error;
  }
};

export const fetchUserId = async () => {
  try {
    const response = await api.get('/auth/userid');
    return response.data;
  } catch (error) {
    console.error('Error fetching user ID:', error);
    throw new Error('Fetching user ID failed');
  }
};

// Weitere API-Funktionen hier...

export const fetchTasks = async (userId) => {
