import React, { useState, useEffect, useCallback, useMemo } from 'react';
import {
  XIcon,
  DeleteIcon,
  AddIcon,
  iconMap,
  lightIconMap,
  customCategoryIcon,
  customCategoryLightIcon
} from '../icons';
import AddCategoryPopUp from './AddCategoryPopUp';
import { addCategory, deleteCategory as apiDeleteCategory, fetchCategories } from '../api';
import { sendWebSocketMessage } from '../websocket';

const initialCategories = [];

function Categories({ onSelectCategory, userId, categoriesFromBackend }) {
  const [categories, setCategories] = useState(initialCategories);
  const [additionalCategories, setAdditionalCategories] = useState(categoriesFromBackend || []);
  const [showDeleteIcons, setShowDeleteIcons] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);

  // Synchronize additionalCategories with categoriesFromBackend
  useEffect(() => {
    if (categoriesFromBackend) {
      setAdditionalCategories(categoriesFromBackend);
    }
  }, [categoriesFromBackend]);

  // Function to update category selection status
  const updateSelection = useCallback((categoryList, categoryId) => 
    categoryList.map(category => ({
      ...category,
      isSelected: category.id === categoryId,
    })),
    []
  );

  // Select category handler
  const selectCategory = async (categoryId) => {
    try {
      // Aktualisiere die Auswahl f체r beide Kategorien
      const updatedCategories = updateSelection(categories, categoryId);
      const updatedAdditionalCategories = updateSelection(additionalCategories, categoryId);

      // Setze die aktualisierten Kategorien
      setCategories(updatedCategories);
      setAdditionalCategories(updatedAdditionalCategories);

      // Finde die ausgew채hlte Kategorie
      const selectedCategory = [...updatedCategories, ...updatedAdditionalCategories].find(category => category.id === categoryId);
      console.log('Selected category:', selectedCategory);
      onSelectCategory(selectedCategory);

      // Fetch updated categories
      const fetchedCategories = await fetchCategories(userId);
      console.log('Fetched categories:', fetchedCategories);

      // Konvertiere die abgerufenen Kategorien
      const processedCategories = fetchedCategories.map(category => ({
        id: category.categoryId,
        name: category.name,
        icon: customCategoryIcon,
        lightIcon: customCategoryLightIcon,
        color: 'text-gray-400',
        selectedColor: 'text-white',
        isSelected: categoryId === category.categoryId, // Setze die Auswahl f체r die gefetchten Kategorien
        fullWidth: false,
        showDelete: false,
        userId: category.userId,
      }));

      // Aktualisiere die zus채tzlichen Kategorien
      setAdditionalCategories(processedCategories);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };


  // Add category handler
  const handleAddCategory = async (categoryData) => {
    try {
      const data = await addCategory({ ...categoryData, userId });
      console.log('Category added:', data);
      const newCategory = {
        id: data.categoryId,
        name: data.name,
        icon: customCategoryIcon,
        lightIcon: customCategoryLightIcon,
        color: 'text-gray-400',
        selectedColor: 'text-white',
        isSelected: false,
        fullWidth: false,
        showDelete: false,
        userId: data.userId,
      };

      setAdditionalCategories(prevCategories => {
        if (!prevCategories.some(cat => cat.id === newCategory.id)) {
          return [...prevCategories, newCategory];
        }
        return prevCategories;
      });

      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_ADD', category: newCategory });
    } catch (error) {
      console.error('Error adding category:', error);
    } finally {
      setShowAddCategoryPopUp(false);
    }
  };

  // Toggle delete icons visibility
  const toggleDeleteIcons = useCallback(() => {
    const updatedCategories = categories.map(category => ({
      ...category,
      showDelete: !showDeleteIcons,
    }));

    const updatedAdditionalCategories = additionalCategories.map(category => ({
      ...category,
      showDelete: !showDeleteIcons,
    }));

    setCategories(updatedCategories);
    setAdditionalCategories(updatedAdditionalCategories);
    setShowDeleteIcons(prev => !prev);
  }, [categories, additionalCategories, showDeleteIcons]);

  // Delete category handler
  const handleDeleteCategory = async (categoryId) => {
    try {
      await apiDeleteCategory(categoryId);
      setAdditionalCategories(prev => prev.filter(cat => cat.id !== categoryId));

      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_DELETE', categoryId });
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  // Combine and deduplicate categories
  const allCategories = useMemo(() => {
    const combinedCategories = [...categories, ...additionalCategories];
    return combinedCategories.reduce((unique, category) => {
      if (!unique.some(cat => cat.id === category.id)) {
        // Bestimme das Icon basierend auf dem Namen der Kategorie
        const icon = iconMap[category.name] || customCategoryIcon;
        const lightIcon = lightIconMap[category.name] || customCategoryLightIcon;

        unique.push({
          id: category.id,
          name: category.name,
          isSelected: category.isSelected || false,
          icon: icon,
          lightIcon: lightIcon,
          color: category.color || 'text-gray-400',
          selectedColor: category.selectedColor || 'text-white',
          fullWidth: category.fullWidth || false,
          showDelete: category.showDelete || false,
        });
      }
      return unique;
    }, []);
  }, [categories, additionalCategories]);


  const isMaxCategories = allCategories.length >= 12;

  // Function to get category styles
  const getCategoryStyles = (category) => ({
    minWidth: category.fullWidth ? '100%' : '140px',
    maxWidth: '100%',
    height: category.fullWidth ? '60px' : '100px',
    borderRadius: '20px',
    backgroundColor: category.isSelected ? '#0374FA' : '#0000000D',
    ringColor: category.isSelected ? '#0374FA26' : '',
    padding: category.fullWidth ? '0 15px' : '15px',
  });

  return (
    <div>
      <div className="grid grid-cols-2 gap-4">
            {allCategories.map((category) => {
        const textColor = category.isSelected ? 'text-white' : 'text-gray-900';
        const IconComponent = category.isSelected ? category.lightIcon : category.icon;

        return (
          <div
            key={category.id} // Hier `category.id` sollte `category.categoryId` sein, wenn du das erwartest
            className={`relative flex ${category.fullWidth ? 'flex-row' : 'flex-col'} justify-center items-center rounded-lg ${category.isSelected ? 'ring-2 ring-offset-2' : ''} ${category.fullWidth ? 'col-span-2' : ''}`}
            style={getCategoryStyles(category)}
            onClick={() => selectCategory(category.id)}
          >
            {category.fullWidth ? (
              <>
                <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                <span className={`ml-2 text-sm font-medium ${textColor}`}>{category.name}</span>
              </>
            ) : (
              <>
                <div className="absolute top-2 left-2 p-2">
                  <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                </div>
                <span className={`absolute bottom-2 left-2 p-2 text-sm font-medium ${textColor}`}>{category.name}</span>
              </>
            )}
            {category.showDelete && (
              <button className="absolute top-2 right-2 p-2" onClick={(e) => { e.stopPropagation(); handleDeleteCategory(category.id); }}>
                <XIcon className="h-5 w-5" />
              </button>
            )}
          </div>
        );
      })}


        <div className="col-span-2 flex items-center justify-center mt-3 gap-3">
          <div className={`flex-grow flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer h-[50px] ${isMaxCategories ? 'opacity-50' : ''}`}
            style={{
              borderRadius: '20px',
              backgroundColor: '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={() => setShowAddCategoryPopUp(true)}
          >
            <div className="flex items-center space-x-2 w-full justify-center">
              <div className="flex items-center justify-center" style={{ width: '20px', height: '20px' }}>
                <AddIcon className="h-6 w-6 text-gray-400" />
              </div>
              <span className="text-sm font-medium text-gray-900">Add Category</span>
            </div>
          </div>
          <div className="flex-shrink-0 flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer w-[50px] h-[50px]"
            style={{
              borderRadius: '20px',
              backgroundColor: showDeleteIcons ? '#FC5E5E26' : '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={toggleDeleteIcons}
          >
            <div className="flex items-center justify-center" style={{ width: '20px', height: '20px' }}>
              <DeleteIcon className="h-6 w-6" />
            </div>
          </div>
        </div>
      </div>
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
          userId={userId}
        />
      )}
    </div>
  );
}

export default Categories;
