import React, { useEffect, useState, useCallback } from 'react';
import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

const WebSocketTestComponent = () => {
  const [messages, setMessages] = useState([]);
  const [task, setTask] = useState({ text: '', deadline: '', planDate: '', userId: 1, categoryId: 1 });

  let stompClient = null;

  const connectWebSocket = useCallback(() => {
    const socket = new SockJS(SOCKET_URL);
    stompClient = Stomp.over(socket);

    const token = localStorage.getItem('jwtToken');
    const headers = token ? { Authorization: `Bearer ${token}` } : {};

    stompClient.connect(headers, () => {
      console.log('Connected to WebSocket');

      // Subscribe to user-specific queue for tasks
      stompClient.subscribe('/user/1/queue/tasks', (message) => {
        handleWebSocketMessage(message);
      });
      console.log(`Subscribed to /user/1/queue/tasks`);
    }, (error) => {
      console.error('Error connecting to WebSocket:', error);
      setTimeout(() => connectWebSocket(), 5000); // Retry connection
    });
  }, []);

  const handleWebSocketMessage = (message) => {
    console.log('Received raw message: ', message);
    if (message.body) {
      try {
        const parsedMessage = JSON.parse(message.body);
        console.log('Parsed message: ', parsedMessage);
        setMessages((prevMessages) => [...prevMessages, parsedMessage]);
      } catch (error) {
        console.error('Failed to parse WebSocket message:', error);
      }
    } else {
      console.error('Message body is missing or undefined');
    }
  };

  const disconnectWebSocket = useCallback(() => {
    if (stompClient) {
      stompClient.disconnect(() => {
        console.log('Disconnected from WebSocket');
      });
    }
  }, []);

  useEffect(() => {
    connectWebSocket();

    return () => {
      disconnectWebSocket();
    };
  }, [connectWebSocket, disconnectWebSocket]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendAddTaskMessage(task);
  };

  const sendAddTaskMessage = (task) => {
    const message = { ...task, type: 'TASK_CREATE' };
    sendWebSocketMessage(`/app/addTask`, message);
  };

  const sendWebSocketMessage = (destination, message) => {
    if (stompClient && stompClient.connected) {
      const token = localStorage.getItem('jwtToken');
      const headers = token ? { Authorization: `Bearer ${token}` } : {};
      console.log('Sending message to', destination);
      try {
        stompClient.send(destination, headers, JSON.stringify(message));
      } catch (error) {
        console.error('Failed to send WebSocket message:', error);
      }
    } else {
      console.error('WebSocket is not connected');
    }
  };

  return (
    <div>
      <h2>WebSocket Test Component</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Text:</label>
          <input type="text" name="text" value={task.text} onChange={handleChange} required />
        </div>
        <div>
          <label>Deadline:</label>
          <input type="date" name="deadline" value={task.deadline} onChange={handleChange} required />
        </div>
        <div>
          <label>Plan Date:</label>
          <input type="date" name="planDate" value={task.planDate} onChange={handleChange} required />
        </div>
        <button type="submit">Add Task</button>
      </form>
      <ul>
        {messages.map((message, index) => (
          <li key={index}>{JSON.stringify(message)}</li>
        ))}
      </ul>
    </div>
  );
};

export default WebSocketTestComponent;
