import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api'; // Adjust this according to your backend setup

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Fetch all todos
export const fetchTodos = async (category) => {
  const response = await api.get('/todos', { params: { category } });
  return response.data;
};

// Add a new task
export const addTask = async (task) => {
  const response = await api.post('/add-task', task);
  return response.data;
};

// Update a task
export const updateTask = async (id, task) => {
  const response = await api.put(`/todos/${id}`, task);
  return response.data;
};

// Delete a task
export const deleteTask = async (id) => {
  await api.delete(`/todos/${id}`);
};

// Generate share token
export const generateShareToken = async (id) => {
  const response = await api.post(`/generate-share-token/${id}`);
  return response.data;
};

// Set task date
export const setTaskDate = async (id, dateRequest) => {
  const response = await api.put(`/set-date/${id}`, dateRequest);
  return response.data;
};

export default api;
