import { useState, useCallback } from 'react';
import { useTasks } from './useTasks';
import { useCategories } from './useCategories';
import { useWebSocket } from './useWebSocket';

export function useDashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  // const [userId, setUserId] = useState(null);

  const { tasks, handleAddTask, handleDeleteTask, setTasks } = useTasks();
  const { categories, selectedCategory, setSelectedCategory, handleAddCategory } = useCategories();

  const handleWebSocketMessage = useCallback((message) => {
    console.log('Received WebSocket message:', message);
    // Handle WebSocket message
  }, []);

  useWebSocket(handleWebSocketMessage);

  const handleCheckboxChange = async (taskId) => {
    const updatedTodos = tasks.map(todo => {
      if (todo.taskId === taskId) {
        const updatedTodo = { ...todo, completed: !todo.completed };
        if (updatedTodo.completed) {
          updatedTodo.categoryId = 7; // Logbook category
        }
        return updatedTodo;
      }
      return todo;
    });

    setTasks(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === taskId);
    try {
      sendWebSocketMessage(`/app/updateTask/${taskId}`, { ...updatedTask });
    } catch (error) {
      console.error('Error sending WebSocket message:', error);
    }
  };

  const handleDateChange = async (taskId, date, purpose) => {
    const updatedTodos = tasks.map(todo => {
      if (todo.taskId === taskId) {
        return {
          ...todo,
          [`${purpose}Date`]: date ? new Date(date).toISOString() : null,
        };
      }
      return todo;
    });

    setTasks(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === taskId);
    try {
      sendWebSocketMessage(`/app/updateTask/${taskId}`, { ...updatedTask });
    } catch (error) {
      console.error('Error sending WebSocket message for date update:', error);
    }
  };

  return {
    showEditTaskPopUp,
    showAddTaskPopUp,
    showAddCategoryPopUp,
    isDeleteMode,
    currentTask,
    setCurrentTask,
    setShowEditTaskPopUp,
    setShowAddTaskPopUp,
    setShowAddCategoryPopUp,
    setIsDeleteMode,
    handleAddTask,
    handleDeleteTask,
    handleCheckboxChange,
    handleDateChange,
    handleAddCategory,
    tasks,
    categories,
    selectedCategory,
    setSelectedCategory,
  };
}
