import axios from 'axios';
import Cookies from 'js-cookie';

const api = axios.create({
  baseURL: 'http://localhost:8080/api/v1',
  withCredentials: true, // Stellen Sie sicher, dass Cookies bei jeder Anfrage gesendet werden
});

// Axios Interceptor, um den JWT-Token bei jeder Anfrage hinzuzufügen
api.interceptors.request.use(
  config => {
    const token = Cookies.get('jwtToken');
    console.debug('Interceptor: JWT token from cookies:', token);
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
      console.debug('Interceptor: Authorization header set with token');
    }
    return config;
  },
  error => {
    return Promise.reject(error);
  }
);

export const loginUser = async (credentials) => {
  try {
    const response = await api.post('/auth/login', credentials);
    const token = response.data;
    if (token) {
      Cookies.set('jwtToken', token); // Speichern Sie den JWT-Token in den Cookies
      const user = await fetchUserId(); // Benutzerdaten abrufen
      Cookies.set('userId', user.userId); // Speichern Sie die Benutzer-ID in den Cookies
    }
    return response.data;
  } catch (error) {
    console.error('Error logging in:', error);
    throw new Error('Login failed');
  }
};

export const fetchUserId = async () => {
  try {
    const response = await api.get('/auth/userid');
    return response.data;
  } catch (error) {
    console.error('Error fetching user ID:', error);
    throw new Error('Fetching user ID failed');
  }
};

export const fetchTasks = (userId) => api.get(`/task/getTasks?userId=${userId}`).then(res => res.data);

export const addTask = (task) => api.post('/task/save', task).then(res => res.data);

export const updateTask = (task) => api.put('/task/update', task).then(res => res.data);

export const deleteTask = (id) => api.delete(`/task/delete/${id}`).then(res => res.data);

// Benutzer
export const fetchUser = (userId) => api.get(`/user/${userId}`).then(res => res.data);

export const registerUser = (user) => api.post('/auth/register', user).then(res => res.data);

export const updateUser = (user) => api.put('/user/update', user).then(res => res.data);

export const deleteUser = (id) => api.delete(`/user/deleteuser/${id}`).then(res => res.data);

// Kategorien
export const addCategory = (category) => api.post('/category/save', category).then(res => res.data);

// Tokens
export const generateTokenForCategory = async (categoryId) => {
  try {
    const response = await api.post('/token/generateForCategory', { categoryId });
    return response.data;
  } catch (error) {
    console.error('Error generating token for category:', error);
    throw error;
  }
};

export const generateTokenForTask = async (taskId) => {
  try {
    const response = await api.post('/token/generateForTask', { taskId });
    return response.data;
  } catch (error) {
    console.error('Error generating token for task:', error);
    throw error;
  }
};
