import React, { useState, useEffect } from 'react';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import AddTaskPopUp from '../components/AddTaskPopUp';
import EditTaskPopUp from '../components/EditTaskPopUp'; // Import the EditTaskPopUp
import { PlannedIcon } from '../icons';
import { fetchUser, addTask, updateTask, deleteTask, setTaskDate } from '../api'; // Import your API functions


function Dashboard() {
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false); // State for EditTaskPopUp
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [todos, setTodos] = useState([]); // Initialize todos as an empty array
  const [selectedCategory, setSelectedCategory] = useState({ id: null, name: 'All Tasks', icon: PlannedIcon });
  const [currentTodo, setCurrentTodo] = useState(null); // State for the currently edited todo
  const [creatorUserId, setCreatorUserId] = useState(null);

  const initialCategories = [
    { id: 1, name: 'All Tasks', icon: PlannedIcon },
    { id: 2, name: 'Today', icon: PlannedIcon },
    { id: 3, name: 'Planned', icon: PlannedIcon },
    { id: 4, name: 'Any Time', icon: PlannedIcon },
    { id: 5, name: 'Work', icon: PlannedIcon },
    { id: 6, name: 'Personal', icon: PlannedIcon },
    { id: 7, name: 'Logbook', icon: PlannedIcon },
  ];
  useEffect(() => {
    const loadUser = async () => {
      try {
        const user = await fetchUser();
        setCreatorUserId(user.id);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    loadUser();
  }, []);

  useEffect(() => {
    const loadTodos = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/todos?category=${encodeURIComponent(selectedCategory.name)}`, {
          credentials: 'include' // Füge dies hinzu, um Cookies zu senden
        });
        if (response.ok) {
          const data = await response.json();
          setTodos(data);
        } else {
          throw new Error('Failed to fetch todos');
        }
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };

    loadTodos();
  }, [selectedCategory]);

  const handleAddTask = async (newTask) => {
    try {
      newTask.creatorUserId = creatorUserId;
      const data = await addTask(newTask);
      setTodos([...todos, data]);
      setShowAddTaskPopUp(false);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleEditTask = async (updatedTodo) => {
    try {
      const data = await updateTask(updatedTodo.id, updatedTodo);
      const updatedTodos = todos.map(todo => (todo.id === updatedTodo.id ? data : todo));
      setTodos(updatedTodos);
      setShowEditTaskPopUp(false);
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(!isDeleteMode);
  };

  const handleEditTodo = (todo) => {
    setCurrentTodo(todo);
    setShowEditTaskPopUp(true);
  };

  const handleDateChange = async (id, date, purpose) => {
    try {
      const dateRequest = { date, purpose };
      const data = await setTaskDate(id, dateRequest);
      const updatedTodos = todos.map(todo => (todo.id === id ? data : todo));
      setTodos(updatedTodos);
    } catch (error) {
      console.error('Error setting date:', error);
    }
  };

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories onSelectCategory={setSelectedCategory} />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              <selectedCategory.icon className="h-6 w-6" />
              <span className="font-bold text-grayCustom-dark">{selectedCategory.name}</span>
            </div>
            <ToDoList
              todos={todos}
              setTodos={setTodos}
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTodo}
              onDateChange={handleDateChange}
              selectedCategory={selectedCategory} // Pass the selected category
            />
            <ToDoActions toggleAddTaskModal={() => setShowAddTaskPopUp(true)} toggleDeleteMode={toggleDeleteMode} />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp onAddTask={handleAddTask} onClose={() => setShowAddTaskPopUp(false)} currentCategoryId={selectedCategory.id} creatorUserId={creatorUserId} />
      )}
      {showEditTaskPopUp && currentTodo && (
        <EditTaskPopUp todo={currentTodo} onSaveTask={handleEditTask} onClose={() => setShowEditTaskPopUp(false)} />
      )}
    </div>
  );
}

export default Dashboard;
