export const STANDARD_CATEGORIES = [
  { id: 1, name: 'All Tasks' },
  { id: 2, name: 'Today' },
  { id: 3, name: 'Planned' },
  { id: 4, name: 'Any Time' },
  { id: 5, name: 'Work' },
  { id: 6, name: 'Personal' },
  { id: 7, name: 'Logbook' },
];
export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  // Erstellen Sie ein Set von Kategorie-IDs für einen schnellen Zugriff
  const standardCategoryIds = new Set(STANDARD_CATEGORIES.map(category => category.id));

  // Initialisiere categorizedTasks für jede Kategorie-ID
  categories.forEach(category => {
    categorizedTasks[category.id] = [];
  });

  tasks.forEach(task => {
    const categoryId = task.categoryId;

    // Prüfen, ob die Aufgabe eine bekannte Kategorie-ID hat oder benutzerdefiniert ist
    if (standardCategoryIds.has(categoryId) || categorizedTasks[categoryId]) {
      switch (categoryId) {
        case 1:
          // All Tasks
          categorizedTasks[1] = tasks;
          break;
        case 2:
          // Today
          categorizedTasks[2] = tasks.filter(task => task.categoryId === 2);
        case 3:
          // Planned
          categorizedTasks[3] = tasks.filter(task => task.planDate);
          break;
        case 4:
          // Any Time
          categorizedTasks[4] = tasks.filter(task => task.categoryId === 4);
          break;
        case 5:
          // Work
          categorizedTasks[5] = tasks.filter(task => task.categoryId === 5);
          break;
        case 6:
          // Personal
          categorizedTasks[6] = tasks.filter(task => task.categoryId === 6);
          break;
        case 7:
          // Logbook
          categorizedTasks[7] = tasks.filter(task => task.completed);
          break;
        default:
          // Benutzerdefinierte Kategorien
          categorizedTasks[categoryId] = categorizedTasks[categoryId] || [];
          categorizedTasks[categoryId].push(task);
          break;
      }
    }
  });

  return categorizedTasks;
};
