import React, { useState, useEffect } from 'react';
import {
  AllTasksIcon,
  AnyTimeIcon,
  PersonalIcon,
  AddIcon,
  PlannedIcon,
  TodayIcon,
  WorkIcon,
  SparkleIcon,
  XIcon,
  DeleteIcon,
  AllTasksLightIcon,
  AnyTimeLightIcon,
  PersonalLightIcon,
  PlannedLightIcon,
  TodayLightIcon,
  WorkLightIcon,
  SparkleLightIcon,
  BookIcon,
  BookLightIcon
} from '../icons';
import AddCategoryPopUp from './AddCategoryPopUp';
import { addCategory, deleteCategory as apiDeleteCategory, fetchCategories } from '../api';
import { sendWebSocketMessage } from '../websocket';

const initialCategoriesStyling = {
  'All Tasks': { icon: AllTasksIcon, lightIcon: AllTasksLightIcon, color: 'text-gray-400', selectedColor: 'text-white' },
  'Today': { icon: TodayIcon, lightIcon: TodayLightIcon, color: 'text-white', selectedColor: 'text-white' },
  'Planned': { icon: PlannedIcon, lightIcon: PlannedLightIcon, color: 'text-pink-500', selectedColor: 'text-white' },
  'Any Time': { icon: AnyTimeIcon, lightIcon: AnyTimeLightIcon, color: 'text-orange-500', selectedColor: 'text-white' },
  'Work': { icon: WorkIcon, lightIcon: WorkLightIcon, color: 'text-red-500', selectedColor: 'text-white' },
  'Personal': { icon: PersonalIcon, lightIcon: PersonalLightIcon, color: 'text-blue-500', selectedColor: 'text-white' },
  'Logbook': { icon: BookIcon, lightIcon: BookLightIcon, color: 'text-green-500', selectedColor: 'text-white' },
};

const customCategoryIcon = SparkleIcon;
const customCategoryLightIcon = SparkleLightIcon;

export default function Categories({ onSelectCategory, userId }) {
  const [categories, setCategories] = useState([]);
  const [showDeleteIcons, setShowDeleteIcons] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);

  useEffect(() => {
    const fetchUserCategories = async () => {
      if (userId) {
        try {
          console.log('Fetching categories for user ID:', userId);
          const fetchedCategories = await fetchCategories(userId);
          console.log('Fetched categories:', fetchedCategories);

          const styledCategories = fetchedCategories.map(category => {
            const style = initialCategoriesStyling[category.name] || { icon: customCategoryIcon, lightIcon: customCategoryLightIcon, color: 'text-gray-400', selectedColor: 'text-white' };
            console.log(`Styling for category "${category.name}":`, style);

            return {
              ...category,
              ...style,
              id: category.categoryId, // Ensure id is set correctly
              isSelected: false,
              fullWidth: false,
              showDelete: false,
            };
          });
          console.log('Styled categories:', styledCategories);
          setCategories(styledCategories);
        } catch (error) {
          console.error('Error fetching categories:', error);
        }
      }
    };
    fetchUserCategories();
  }, [userId]);

  const selectCategory = (categoryId) => {
    console.log('Selecting category with ID:', categoryId);
    const updatedCategories = categories.map(category => ({
      ...category,
      isSelected: category.id === categoryId,
    }));
    console.log('Updated categories after selection:', updatedCategories);
    setCategories(updatedCategories);

    const selectedCategory = updatedCategories.find(category => category.id === categoryId);
    console.log('Selected category:', selectedCategory);
    onSelectCategory(selectedCategory);
  };

  const handleAddCategory = async (categoryData) => {
    try {
      console.log('Adding new category with data:', categoryData);
      const data = await addCategory(categoryData);
      console.log('Category added:', data);

      const style = initialCategoriesStyling[data.name] || { icon: customCategoryIcon, lightIcon: customCategoryLightIcon, color: 'text-gray-400', selectedColor: 'text-white' };
      console.log(`Styling for new category "${data.name}":`, style);

      const newCategory = {
        ...data,
        ...style,
        id: data.categoryId, // Ensure id is set correctly
        isSelected: false,
        fullWidth: false,
        showDelete: false,
      };
      console.log('New category with styling:', newCategory);

      setCategories(prevCategories => [...prevCategories, newCategory]);
      console.log('Categories after adding new category:', [...categories, newCategory]);

      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_ADD', category: newCategory });
      console.log('WebSocket message sent for CATEGORY_ADD:', newCategory);
    } catch (error) {
      console.error('Error adding category:', error);
    } finally {
      setShowAddCategoryPopUp(false);
    }
  };

  const toggleDeleteIcons = () => {
    const updatedCategories = categories.map(category => ({
      ...category,
      showDelete: !showDeleteIcons,
    }));
    console.log('Toggled delete icons:', updatedCategories);
    setCategories(updatedCategories);
    setShowDeleteIcons(!showDeleteIcons);
  };

  const handleDeleteCategory = async (categoryId) => {
    try {
      console.log('Deleting category with ID:', categoryId);
      await apiDeleteCategory(categoryId);
      setCategories(categories.filter(cat => cat.id !== categoryId));
      console.log('Categories after deletion:', categories);

      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_DELETE', categoryId });
      console.log('WebSocket message sent for CATEGORY_DELETE:', categoryId);
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  return (
    <div>
      <div className="grid grid-cols-2 gap-4">
        {categories.map((category) => {
          const bgColor = category.isSelected ? '#0374FA' : '#0000000D';
          const ringColor = category.isSelected ? '#0374FA26' : '';
          const textColor = category.isSelected ? 'text-white' : 'text-gray-900';
          const IconComponent = category.isSelected ? category.lightIcon : category.icon;

          return (
            <div
              key={category.id}
              className={`relative flex ${category.fullWidth ? 'flex-row' : 'flex-col'} justify-center items-center rounded-lg ${category.isSelected ? 'ring-2 ring-offset-2' : ''} ${category.fullWidth ? 'col-span-2' : ''}`}
              style={{
                minWidth: category.fullWidth ? '100%' : '140px',
                maxWidth: '100%',
                height: category.fullWidth ? '60px' : '100px',
                borderRadius: '20px',
                backgroundColor: bgColor,
                ringColor: ringColor,
                padding: category.fullWidth ? '0 15px' : '15px',
              }}
              onClick={() => selectCategory(category.id)}
            >
              {category.fullWidth ? (
                <>
                  <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                  <span className={`ml-2 text-sm font-medium ${textColor}`}>{category.name}</span>
                </>
              ) : (
                <>
                  <div className="absolute top-2 left-2 p-2">
                    <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                  </div>
                  <span className={`absolute bottom-2 left-2 p-2 text-sm font-medium ${textColor}`}>{category.name}</span>
                </>
              )}
              {category.showDelete && (
                <button className="absolute top-2 right-2 p-2" onClick={(e) => { e.stopPropagation(); handleDeleteCategory(category.id); }}>
                  <XIcon className="h-5 w-5" />
                </button>
              )}
            </div>
          );
        })}
        <div className="col-span-2 flex items-center justify-center mt-3 gap-3">
          <div className={`flex-grow flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer h-[50px] ${categories.length >= 12 ? 'opacity-50' : ''}`}
            style={{
              borderRadius: '20px',
              backgroundColor: '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={() => setShowAddCategoryPopUp(true)}
          >
            <div className="flex items-center space-x-2 w-full justify-center">
              <div className="flex items-center justify-center" style={{ width: '20px', height: '20px' }}>
                <AddIcon className="h-6 w-6 text-gray-400" />
              </div>
              <span className="text-sm font-medium text-gray-900">Add Category</span>
            </div>
          </div>
          <div className="flex-shrink-0 flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer w-[50px] h-[50px]"
            style={{
              borderRadius: '20px',
              backgroundColor: showDeleteIcons ? '#FC5E5E26' : '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={toggleDeleteIcons}
          >
            <div className="flex items-center justify-center" style={{ width: '20px', height: '20px' }}>
              <DeleteIcon className="h-6 w-6 text-red-500" />
            </div>
          </div>
        </div>
      </div>
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
          userId={userId}
        />
      )}
    </div>
  );
}
