import React, { useState, useEffect } from 'react';
import { connectWebSocket, disconnectWebSocket, sendUpdateTaskMessage } from '../websocket';

function HelloWebSocket() {
    const [message, setMessage] = useState('');
    const [response, setResponse] = useState('');

    useEffect(() => {
        const handleWebSocketMessage = (message) => {
            console.log('Received WebSocket message:', message);
            setResponse(message);

            // Send a response back
            const responseMessage = {
                text: 'Received your message',
                originalMessage: message
            };
            sendUpdateTaskMessage(message.taskId, responseMessage);
        };

        connectWebSocket(handleWebSocketMessage);

        return () => {
            disconnectWebSocket();
        };
    }, []);

    const handleSendMessage = () => {
        const taskId = 1; // Beispiel task ID
        const taskDTO = {
            userId: 1,
            taskId: taskId,
            text: message,
            deadlineDate: null,
            planDate: null,
            categoryId: 1,
            completed: false
        };

        sendUpdateTaskMessage(taskDTO);
    };

    return (
        <div>
            <h1>Hello WebSocket</h1>
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Enter a message"
            />
            <button onClick={handleSendMessage}>Send Update Task Message</button>
            <h2>Response from server:</h2>
            <pre>{JSON.stringify(response, null, 2)}</pre>
        </div>
    );
}

export default HelloWebSocket;
