import React, { useEffect, useState, useCallback } from 'react';
import ToDo from './ToDo';
import { fetchTasks } from '../api';
import { startOfToday, isAfter } from 'date-fns';
import { sendWebSocketMessage } from '../websocket';

export default function ToDoList({ todos = [], setTasks, isDeleteMode, onEditTodo, selectedCategory, handleDeleteTask, userId }) {
  const [selectedTodoId, setSelectedTodoId] = useState(null);

  // Fetch todos from API
  const fetchTodos = useCallback(async () => {
    try {
      console.log(`Fetching todos for user ID: ${userId}`);
      const data = await fetchTasks(); // API call to fetch tasks
      console.log('Fetched todos:', data);
      setTasks(data); // Update the tasks state
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  }, [userId, setTasks]);

  useEffect(() => {
    fetchTodos(); // Fetch todos on component mount or userId change
  }, [userId, fetchTodos]);

  // Handle checkbox change (completion status)
  const handleCheckboxChange = async (taskId) => {
    const updatedTodos = todos.map(todo => {
      if (todo.taskId === taskId) {
        const updatedTodo = { ...todo, completed: !todo.completed };
        if (updatedTodo.completed) {
          updatedTodo.categoryId = 7; // Logbook category
        }
        return updatedTodo;
      }
      return todo;
    });

    setTasks(updatedTodos); // Update the tasks state

    const updatedTask = updatedTodos.find(todo => todo.taskId === taskId);
    try {
      console.log('Sending WebSocket message to update task:', { ...updatedTask, userId });
      sendWebSocketMessage(`/app/updateTask/${taskId}`, { ...updatedTask, userId }); // Send WebSocket message
    } catch (error) {
      console.error('Error sending WebSocket message:', error);
    }
  };

  // Handle date changes
  const handleDateChange = async (taskId, date, purpose) => {
    const updatedTodos = todos.map(todo => {
      if (todo.taskId === taskId) {
        return {
          ...todo,
          [`${purpose}Date`]: date ? new Date(date).toISOString() : null, // Update the date field
        };
      }
      return todo;
    });

    setTasks(updatedTodos); // Update the tasks state

    const updatedTask = updatedTodos.find(todo => todo.taskId === taskId);
    try {
      console.log('Sending WebSocket message to update task date:', { ...updatedTask, userId });
      sendWebSocketMessage(`/app/updateTask/${taskId}`, { ...updatedTask, userId }); // Send WebSocket message
    } catch (error) {
      console.error('Error sending WebSocket message for date update:', error);
    }
  };

  // Handle delete task
  const handleDeleteClick = async (taskId) => {
    console.log('Deleting task with ID:', taskId);
    try {
      await handleDeleteTask(taskId); // API call to delete task
      sendWebSocketMessage(`/app/deleteTask/${taskId}`, { taskId }); // Send WebSocket message
    } catch (error) {
      console.error('Error deleting task:', error.response ? error.response.data : error.message);
    }
  };

  // Filter todos based on selected category
  const today = startOfToday();
  const filteredTodos = selectedCategory && selectedCategory.name ? todos.filter(todo => {
    switch (selectedCategory.name) {
      case 'Planned':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
      case 'Today':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today) && todo.categoryId !== 7;
      case 'Personal':
      case 'Work':
      case 'Any Time':
        return todo.categoryId === selectedCategory.id;
      case 'All Tasks':
        return !todo.planDate && todo.categoryId !== 7;
      case 'Logbook':
        return todo.categoryId === 7;
      default:
        return todo.categoryId === selectedCategory.id;
    }
  }) : todos;

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => (
          <ToDo
            key={todo.taskId} // Ensure unique ID
            todo={todo}
            handleCheckboxChange={() => handleCheckboxChange(todo.taskId)}
            handleTodoClick={() => setSelectedTodoId(todo.taskId === selectedTodoId ? null : todo.taskId)}
            handleDelete={() => handleDeleteClick(todo.taskId)}
            handleEditClick={() => onEditTodo ? onEditTodo(todo) : null}
            isSelected={todo.taskId === selectedTodoId}
            onDateChange={handleDateChange}
            isDeleteMode={isDeleteMode}
            userId={userId}
          />
        ))}
      </div>
    </fieldset>
  );
}
