import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

export const fetchTodos = async (category) => {
  const response = await axios.get(`${API_BASE_URL}/todos?category=${category}`, { withCredentials: true });
  return response.data;
};

export const addTask = async (task) => {
  const response = await axios.post(`${API_BASE_URL}/add-task`, task, { withCredentials: true });
  return response.data;
};

export const updateTask = async (id, task) => {
  const response = await axios.put(`${API_BASE_URL}/todos/${id}`, task, { withCredentials: true });
  return response.data;
};

export const deleteTask = async (id) => {
  const response = await axios.delete(`${API_BASE_URL}/todos/${id}`, { withCredentials: true });
  return response.data;
};

export const generateShareToken = async (id) => {
  const response = await axios.post(`${API_BASE_URL}/generate-share-token/${id}`, { withCredentials: true });
  return response.data;
};

export const setTaskDate = async (id, dateRequest) => {
  const response = await axios.put(`${API_BASE_URL}/set-date/${id}`, dateRequest, { withCredentials: true });
  return response.data;
};
