import React, { useState, useEffect } from 'react';
import ToDo from './ToDo';
import { startOfToday, isSameDay, isAfter, isBefore } from 'date-fns';
import { deleteTask, updateTask } from '../api'; // Importiere die API-Funktion

export default function ToDoList({ todos = [], setTodos, isDeleteMode, onEditTodo, selectedCategory, fetchTasksData }) {
  const [selectedTodoId, setSelectedTodoId] = useState(null);

  useEffect(() => {
    console.log('Todos updated:', todos);
  }, [todos]);

  const handleCheckboxChange = async (id) => {
    console.log('Checkbox changed for ID:', id);
    const updatedTodos = todos.map(todo =>
      todo.taskId === id ? { ...todo, completed: !todo.completed } : todo
    );
    setTodos(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === id);
    try {
      await updateTask(updatedTask);
      console.log('Fetching tasks after checkbox change...');
      await fetchTasksData(); // Fetch tasks after update
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  const handleDeleteClick = async (id) => {
    console.log('Deleting task with ID:', id);
    try {
      await deleteTask(id); // Aufruf der API-Funktion zum Löschen des Todos
      console.log('Fetching tasks after delete...');
      await fetchTasksData(); // Fetch tasks after delete
    } catch (error) {
      console.error('Error deleting task:', error.response ? error.response.data : error.message);
      // Handle error (e.g., show error message to user)
    }
  };

  const handleTodoClick = (id) => {
    console.log('Todo clicked with ID:', id);
    setSelectedTodoId(id); // Set selected todo id
  };

  const handleEditClick = (todo) => {
    onEditTodo(todo);
  };

  const handleDateChange = async (id, date, purpose) => {
    const formattedDate = date.toISOString().split('T')[0]; // Format the date as "yyyy-MM-dd"
    console.log(`Updating ${purpose} date to:`, formattedDate);
    try {
      const updatedTodos = todos.map(todo =>
        todo.taskId === id ? { ...todo, [`${purpose}Date`]: formattedDate } : todo
      );
      setTodos(updatedTodos);

      const updatedTask = updatedTodos.find(todo => todo.taskId === id);
      await updateTask(updatedTask);
      console.log('Fetching tasks after date change...');
      await fetchTasksData(); // Fetch tasks after update
    } catch (error) {
      console.error('Error updating date:', error);
    }
  };

  const today = startOfToday();

  const filteredTodos = todos.filter(todo => {
    if (selectedCategory.name === 'Planned') {
      return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
    }
    if (selectedCategory.name === 'Today' || selectedCategory.name === 'Personal' || selectedCategory.name === 'Work' || selectedCategory.name === 'Any Time') {
      return (todo.category === selectedCategory.name && (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)));
    }
    if (selectedCategory.name === 'All Tasks') {
      return (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)) && todo.category !== 'Planned';
    }
    if (selectedCategory.name === 'Logbook') {
      return todo.category === 'Logbook';
    }
    return todo.category === selectedCategory.name;
  });

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => (
          <ToDo
            key={todo.taskId} // Verwenden Sie taskId als Schlüssel
            todo={todo}
            handleCheckboxChange={() => handleCheckboxChange(todo.taskId)}
            handleTodoClick={() => handleTodoClick(todo.taskId)}
            handleDelete={() => handleDeleteClick(todo.taskId)}
            handleEditClick={() => handleEditClick(todo)}
            isDeleteMode={isDeleteMode}
            onDateChange={handleDateChange}
            isSelected={todo.taskId === selectedTodoId} // Pass the selected state
            isTodayCategory={selectedCategory.name === 'Today'}
          />
        ))}
      </div>
    </fieldset>
  );
}
