import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

class WebSocketService {
    constructor() {
        this.stompClient = null;
    }

    connect(onMessageReceived) {
        const socket = new SockJS(SOCKET_URL);
        this.stompClient = Stomp.over(socket);

        const token = localStorage.getItem('jwtToken');
        const headers = token ? { Authorization: `Bearer ${token}` } : {};

        this.stompClient.connect(headers, () => {
            console.log('Connected to WebSocket');

            this.stompClient.subscribe('/topic/tasks', (message) => {
                if (message.body) {
                    console.log('Received message on /topic/tasks:', message); // Debug: Zeige die Nachricht
                    onMessageReceived(JSON.parse(message.body));
                }
            });

            this.stompClient.subscribe('/user/queue/tasks', (message) => {
                if (message.body) {
                    console.log('Received message on /user/queue/tasks:', message); // Debug: Zeige die Nachricht
                    onMessageReceived(JSON.parse(message.body));
                }
            });
        }, (error) => {
            console.error('Error connecting to WebSocket:', error);
            setTimeout(() => this.connect(onMessageReceived), 5000);
        });
    }

    disconnect() {
        if (this.stompClient) {
            this.stompClient.disconnect(() => {
                console.log('Disconnected from WebSocket');
            });
        }
    }

    sendMessage(destination, message) {
        if (this.stompClient && this.stompClient.connected) {
            const token = localStorage.getItem('jwtToken');
            const headers = token ? { Authorization: `Bearer ${token}` } : {};
            console.log('Sending message to', destination, message); // Debug: Zeige die Nachricht und das Ziel
            this.stompClient.send(destination, headers, JSON.stringify(message));
        }
    }
}
