import React, { useEffect, useState } from 'react';
import { connectWebSocket, disconnectWebSocket, sendWebSocketMessage } from './webSocketService';
import ToDo from './ToDo'; // Stellen Sie sicher, dass der Pfad korrekt ist

const ToDoList = ({ userId }) => {
  const [todos, setTodos] = useState([]);
  const [selectedTodoId, setSelectedTodoId] = useState(null);
  const [isDeleteMode, setIsDeleteMode] = useState(false);

  useEffect(() => {
    connectWebSocket(userId, handleMessageReceived);
    fetchTodos();

    return () => {
      disconnectWebSocket();
    };
  }, [userId]);

  const fetchTodos = () => {
    fetch(`/api/v1/task/getTasks?userId=${userId}`)
      .then(response => response.json())
      .then(data => setTodos(data))
      .catch(error => console.error('Error fetching tasks:', error));
  };

  const handleMessageReceived = (message) => {
    console.log("WebSocket message received:", message);
    if (message.type === 'TASK_UPDATE') {
      updateTaskInState(message.task);
    }
  };

  const updateTaskInState = (updatedTask) => {
    setTodos(prevTodos => {
      const todos = prevTodos.map(task =>
        task.taskId === updatedTask.taskId ? updatedTask : task
      );
      return todos;
    });
  };

  const handleCheckboxChange = (taskId) => {
    const task = todos.find(task => task.taskId === taskId);
    if (task) {
      const updatedTask = { ...task, completed: !task.completed };

      fetch(`/api/v1/task/update`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTask),
      })
        .then(response => response.json())
        .then(data => {
          updateTaskInState(data);
          sendWebSocketMessage('/app/updateTask', { task: data });
        })
        .catch(error => console.error('Error updating task:', error));
    }
  };

  const handleDeleteClick = (taskId) => {
    // API-Aufruf zum Löschen der Aufgabe
    fetch(`/api/v1/task/delete/${taskId}`, {
      method: 'DELETE',
    })
      .then(() => {
        setTodos(prevTodos => prevTodos.filter(task => task.taskId !== taskId));
        sendWebSocketMessage('/app/deleteTask', { taskId });
      })
      .catch(error => console.error('Error deleting task:', error));
  };

  const handleDateChange = (taskId, newDate) => {
    const task = todos.find(task => task.taskId === taskId);
    if (task) {
      const updatedTask = { ...task, planDate: newDate };

      fetch(`/api/v1/task/update`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTask),
      })
        .then(response => response.json())
        .then(data => {
          updateTaskInState(data);
          sendWebSocketMessage('/app/updateTask', { task: data });
        })
        .catch(error => console.error('Error updating task date:', error));
    }
  };

  return (
    <div>
      <h1>To-Do List</h1>
      <ul>
        {todos.map(todo => (
          <ToDo
            key={todo.taskId}
            todo={todo}
            handleCheckboxChange={() => handleCheckboxChange(todo.taskId)}
            handleTodoClick={() => setSelectedTodoId(todo.taskId === selectedTodoId ? null : todo.taskId)}
            handleDelete={() => handleDeleteClick(todo.taskId)}
            handleEditClick={() => onEditTodo(todo)}
            isSelected={todo.taskId === selectedTodoId}
            onDateChange={handleDateChange}
            isDeleteMode={isDeleteMode}
            userId={userId}
          />
        ))}
      </ul>
    </div>
  );
};

export default ToDoList;
