import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp';
import {
  AllTasksIcon, AnyTimeIcon, PersonalIcon, PlannedIcon, SparkleIcon, TodayIcon, WorkIcon, BookIcon
} from '../icons';
import {
  fetchTasks, addTask, updateTask, deleteTask, fetchUserId, fetchEmail, addCategory, fetchCategories
} from '../api';
import { connectWebSocket, disconnectWebSocket } from '../websocket';

const iconMap = {
  'All Tasks': AllTasksIcon,
  'Today': TodayIcon,
  'Planned': PlannedIcon,
  'Any Time': AnyTimeIcon,
  'Work': WorkIcon,
  'Personal': PersonalIcon,
  'Logbook': BookIcon,
};

const customCategoryIcon = SparkleIcon;

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();
  const webSocketRef = useRef(null);


  const initializeWebSocket = useCallback((userId) => {
    if (!webSocketRef.current) {
      console.log('Connecting WebSocket with userId:', userId);
      try {
        connectWebSocket(userId, onTaskMessageReceived);
        webSocketRef.current = true; // Mark WebSocket as initialized
      } catch (error) {
        console.error('Error initializing WebSocket:', error);
      }
    } else {
      console.log('WebSocket already connected for userId:', userId);
    }
  }, []);

useEffect(() => {
  const fetchUserAndTasksAndCategories = async () => {
      try {
          const email = await fetchEmail();
          const fetchedUserId = await fetchUserId(email);
          if (fetchedUserId) {
              setUserId(fetchedUserId);
              initializeWebSocket(fetchedUserId);
              const tasks = await fetchTasks();
              setTasks(tasks);
              const fetchedCategories = await fetchCategories(fetchedUserId);
              const mappedCategories = fetchedCategories.map(category => ({
                  ...category,
                  id: category.categoryId,
                  icon: iconMap[category.name] || customCategoryIcon,
              }));
              setCategories(mappedCategories);
              if (mappedCategories.length > 0) {
                  setSelectedCategory(mappedCategories[0]);
              }
          } else {
              navigate('/login');
          }
      } catch (error) {
          navigate('/login');
      }
  };
  fetchUserAndTasksAndCategories();

  return () => {
      if (webSocketRef.current) {
          console.log('Disconnecting WebSocket...');
          disconnectWebSocket();
          webSocketRef.current = null;
          console.log('WebSocket disconnected and reference cleared.');
      } else {
          console.log('WebSocket reference not set, nothing to disconnect.');
      }
  };
}, [initializeWebSocket, navigate]);


  const handleAddNewTask = async (newTask) => {
    try {
      const formattedTask = {
        ...newTask,
        categoryId: Number(newTask.categoryId),
      };
      const addedTask = await addTask(formattedTask, userId);
      setShowAddTaskPopUp(false);
      setTasks(prevTasks => [...prevTasks, addedTask]);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleSaveTask = async (updatedTask) => {
    try {
      const taskToUpdate = {
        ...updatedTask,
        userId: userId,
        taskId: currentTask.taskId,
        categoryId: updatedTask.categoryId ? Number(updatedTask.categoryId) : currentTask.categoryId, // Sicherstellen, dass die categoryId vorhanden ist
      };
      const updated = await updateTask(taskToUpdate, userId);
      setShowEditTaskPopUp(false);
      setTasks(prevTasks => prevTasks.map(task => (task.taskId === updated.taskId ? updated : task)));
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id, userId);
      setTasks(prevTasks => prevTasks.filter(task => task.taskId !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleAddCategory = async (newCategory) => {
    try {
      const categoryExists = categories.some(cat => cat.name === newCategory.name);
      if (categoryExists) {
        console.warn('Category already exists:', newCategory);
        return;
      }
      const data = await addCategory(newCategory);
      const newCategoryObject = {
        id: data.categoryId,
        name: data.name,
        icon: customCategoryIcon,
      };
      setCategories(prevCategories => [...prevCategories, newCategoryObject]);
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(prevMode => !prevMode);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true);
  };


  const onTaskMessageReceived = (message) => {
    console.log('Task message received:', message);
    // Verarbeiten der empfangenen Nachricht
    if (message.type === 'TASK_UPDATE' && message.task) {
      setTasks((prevTasks) => {
        const taskExists = prevTasks.some((t) => t.taskId === message.task.taskId);
        if (taskExists) {
          return prevTasks.map((t) => (t.taskId === message.task.taskId ? message.task : t));
        } else {
          return [...prevTasks, message.task];
        }
      });
    }
  };

const onCategoryMessageReceived = (message) => {
    console.log('Category message received:', message); // Debugging-Log hinzufügen
    const { type, category, categoryId } = message;

    if (type === 'CATEGORY_UPDATE' && category) {
        console.log('Processing CATEGORY_UPDATE:', category); // Debugging-Log hinzufügen
        setCategories(prevCategories =>
            prevCategories.map(c => (c.id === category.categoryId ? { ...category, id: category.categoryId, icon: iconMap[category.name] || customCategoryIcon } : c))
        );
    } else if (type === 'CATEGORY_DELETE' && categoryId) {
        console.log('Processing CATEGORY_DELETE for categoryId:', categoryId); // Debugging-Log hinzufügen
        setCategories(prevCategories => prevCategories.filter(c => c.id !== categoryId));
    } else {
        console.log('Unknown category message type or missing category data:', type);
    }
};


  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories onSelectCategory={setSelectedCategory} userId={userId} categoriesFromBackend={categories} />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              {selectedCategory && selectedCategory.icon && <selectedCategory.icon className="h-6 w-6" />}
              <span className="font-bold text-grayCustom-dark">{selectedCategory?.name}</span>
            </div>
            <ToDoList
              todos={tasks}
              setTasks={setTasks}
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTask}
              selectedCategory={selectedCategory}
              handleDeleteTask={handleDeleteTask}
              userId={userId}
            />
            <ToDoActions
              toggleAddTaskModal={() => setShowAddTaskPopUp(true)}
              toggleDeleteMode={toggleDeleteMode}
              selectedCategoryId={selectedCategory?.id}
              toggleAddCategoryModal={() => setShowAddCategoryPopUp(true)}
              userId={userId}
            />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAddTask={handleAddNewTask}
          categories={categories}
          onClose={() => setShowAddTaskPopUp(false)}
        />
      )}
      {showEditTaskPopUp && (
        <EditTaskPopUp
          task={currentTask}
          onSaveTask={handleSaveTask}
          onClose={() => setShowEditTaskPopUp(false)}
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
        />
      )}
    </div>
  );
}

export default Dashboard;
